import React, { useState } from 'react';
import { MapPin, CreditCard, Smartphone, CheckCircle, Star } from 'lucide-react';

const CheckoutPage = ({ cart, user }) => {
  const [step, setStep] = useState(2); // Step 2: Information
  const [rating, setRating] = useState(0);
  
  const subtotal = cart.reduce((acc, item) => acc + item.price, 0);
  const delivery = 2000; //
  const total = subtotal + delivery;

  // STEP 2: SHIPPING & PAYMENT SELECTION
  if (step === 2 || step === 3) {
    return (
      <div style={pageBg}>
        <div style={checkoutContainer}>
          <div style={leftCol}>
            <h2 style={sectionTitle}>Secure Checkout</h2>
            
            {/* Delivery Form */}
            <div style={card}>
              <h3 style={cardTitle}><MapPin size={18} color="#008ECC"/> Delivery Destination</h3>
              <div style={row}>
                <div style={inputGroup}>
                  <label>Phone Number (Momo)</label>
                  <input type="text" placeholder="078... / 079..." style={input} />
                </div>
                <div style={inputGroup}>
                  <label>District</label>
                  <select style={input}><option>Gasabo</option><option>Kicukiro</option></select>
                </div>
              </div>
              <div style={inputGroup}>
                <label>Specific Street / Landmark</label>
                <input type="text" placeholder="Example: KG 123 St, near the stadium" style={input} />
              </div>
            </div>

            {/* Payment Selection */}
            <div style={card}>
              <h3 style={cardTitle}><CreditCard size={18} color="#008ECC"/> Payment Method</h3>
              <div style={activePayment}>
                <Smartphone size={20} /> <span>Mobile Money</span>
              </div>
            </div>
          </div>

          {/* Order Summary Sidebar */}
          <div style={rightCol}>
            <div style={summaryCard}>
              <h3>Order Summary</h3>
              {cart.map((item, i) => (
                <div key={i} style={itemRow}>
                  <div style={miniBox}></div>
                  <div><p>{item.name}</p><p>RWF {item.price}</p></div>
                </div>
              ))}
              <hr />
              <div style={priceRow}><span>Subtotal</span><span>RWF {subtotal}</span></div>
              <div style={priceRow}><span>Delivery</span><span>RWF {delivery}</span></div>
              <div style={totalRow}><span>Total</span><span>RWF {total}</span></div>
              <button onClick={() => setStep(4)} style={placeOrderBtn}>Place Order Now</button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // STEPS 4 & 5: SUCCESS & RATING
  return (
    <div style={successPage}>
      <CheckCircle size={80} color="#28a745" />
      <h1>Thanks for buying on MegaMart!</h1>
      <p>Your order is confirmed. You will receive a notification shortly.</p>
      
      <div style={ratingCard}>
        <h3>Rate our website</h3>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
          {[1, 2, 3, 4, 5].map(s => (
            <Star key={s} fill={rating >= s ? "#ffc107" : "none"} color="#ffc107" onClick={() => setRating(s)} style={{cursor:'pointer'}}/>
          ))}
        </div>
        {rating > 0 && <p style={{color:'#28a745', fontWeight:'bold', marginTop:'10px'}}>Thank you for your feedback!</p>}
      </div>
    </div>
  );
};

// --- STYLES (Based on your Screenshot image_3c4200.jpg) ---
const pageBg = { backgroundColor: '#f8f9fa', minHeight: '100vh', padding: '40px 10%' };
const checkoutContainer = { display: 'flex', gap: '30px' };
const leftCol = { flex: 2 };
const rightCol = { flex: 1 };
const card = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', marginBottom: '20px', border: '1px solid #eee' };
const inputGroup = { display: 'flex', flexDirection: 'column', margin: '10px 0', flex: 1 };
const input = { padding: '10px', borderRadius: '8px', border: '1px solid #ddd', marginTop: '5px' };
const row = { display: 'flex', gap: '15px' };
const activePayment = { border: '2px solid #008ECC', backgroundColor: '#f0faff', padding: '15px', borderRadius: '10px', display: 'flex', alignItems: 'center', gap: '10px' };
const placeOrderBtn = { width: '100%', backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '15px', borderRadius: '10px', fontWeight: 'bold', fontSize: '16px', cursor: 'pointer' };
const summaryCard = { backgroundColor: 'white', padding: '25px', borderRadius: '15px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' };
const successPage = { textAlign: 'center', padding: '100px 20px' };
const ratingCard = { marginTop: '50px', padding: '30px', border: '1px solid #eee', borderRadius: '20px', maxWidth: '400px', margin: '50px auto' };

export default CheckoutPage;
