import React from 'react';

const Sidebar = ({ isOpen, onClose, user }) => {
  return (
    <>
      {/* Dark Overlay */}
      {isOpen && <div style={overlayStyle} onClick={onClose} />}
      
      {/* Sidebar Content */}
      <div style={{ ...sidebarStyle, left: isOpen ? '0' : '-365px' }}>
        <div style={sidebarHeader}>
          <span>ðŸ‘¤ Hello, {user ? user.email.split('@')[0] : 'Sign In'}</span>
          <button onClick={onClose} style={closeBtn}>âœ•</button>
        </div>

        <div style={sidebarContent}>
          <h3 style={sectionTitle}>Digital Content & Devices</h3>
          <ul style={listStyle}>
            <li>MegaMart Music</li>
            <li>E-readers & Tablets</li>
            <li>MegaMart Appstore</li>
          </ul>

          <h3 style={sectionTitle}>Shop By Department</h3>
          <ul style={listStyle}>
            <li style={listItemStyle}>Smartphones & Tablets <span>â€º</span></li>
            <li style={listItemStyle}>Computers & Laptops <span>â€º</span></li>
            <li style={listItemStyle}>Smart Home & Security <span>â€º</span></li>
            <li style={listItemStyle}>Audio & Headphones <span>â€º</span></li>
            <li style={listItemStyle}>Cameras & Photo <span>â€º</span></li>
            <li style={listItemStyle}>Gaming Consoles <span>â€º</span></li>
          </ul>
        </div>
      </div>
    </>
  );
};

const overlayStyle = { position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', backgroundColor: 'rgba(0,0,0,0.7)', zIndex: 2000 };
const sidebarStyle = { position: 'fixed', top: 0, width: '350px', height: '100vh', backgroundColor: 'white', zIndex: 2001, transition: '0.3s ease', overflowY: 'auto' };
const sidebarHeader = { backgroundColor: '#232f3e', color: 'white', padding: '15px 30px', fontSize: '18px', fontWeight: 'bold', display: 'flex', justifyContent: 'space-between' };
const closeBtn = { background: 'none', border: 'none', color: 'white', fontSize: '20px', cursor: 'pointer' };
const sidebarContent = { padding: '20px 0' };
const sectionTitle = { padding: '0 30px', fontSize: '16px', fontWeight: 'bold', margin: '20px 0 10px 0' };
const listStyle = { listStyle: 'none', padding: 0, borderBottom: '1px solid #eee', paddingBottom: '10px' };
const listItemStyle = { padding: '12px 30px', fontSize: '14px', cursor: 'pointer', display: 'flex', justifyContent: 'space-between', hover: { backgroundColor: '#f3f3f3' } };

export default Sidebar;
