import React, { useState } from 'react';
import { ShieldCheck, ChevronDown, CheckCircle, Star, Globe } from 'lucide-react';

const CheckoutPage = ({ cart, user }) => {
  const [step, setStep] = useState(1);
  const [rating, setRating] = useState(0);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  
  // Example list of countries (You can expand this or use a library like 'world-countries')
  const countries = ['Rwanda', 'United States', 'Kenya', 'United Kingdom', 'Canada', 'France', 'Germany', 'China', 'India'];

  const subtotal = cart.reduce((acc, item) => acc + item.price, 0);
  // International shipping might vary, but we'll use a base global fee for now
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000; 
  const total = subtotal + delivery;

  if (step === 1) {
    return (
      <div style={pageWrapper}>
        <div style={mainContent}>
          <div style={sectionCard}>
            <h2 style={sectionHeader}><Globe size={24} style={{marginRight: '10px'}}/> Shipping Information</h2>
            
            {/* GLOBAL COUNTRY SELECTOR */}
            <div style={formRow}>
              <div style={inputGroupFull}>
                <label style={label}>Country or region</label>
                <select 
                  style={selectInput} 
                  value={selectedCountry}
                  onChange={(e) => setSelectedCountry(e.target.value)}
                >
                  {countries.map(country => (
                    <option key={country} value={country}>{country}</option>
                  ))}
                </select>
              </div>
            </div>
            
            <div style={formRow}>
              <input style={inputHalf} placeholder="First name" />
              <input style={inputHalf} placeholder="Last name" />
            </div>

            <div style={formRow}>
              <input style={inputFull} placeholder="Street address" />
            </div>

            <div style={formRow}>
              <input style={inputThird} placeholder="City" />
              <input style={inputThird} placeholder="State / Province" />
              <input style={inputThird} placeholder="ZIP / Postal code" />
            </div>

            {/* INTERNATIONAL PHONE INPUT */}
            <div style={formRow}>
              <div style={phoneWrapper}>
                <input style={phoneInput} placeholder="International Phone number (e.g. +250...)" />
              </div>
            </div>
            <p style={helperText}>Required for international shipping updates.</p>
            
            <button style={doneBtn} onClick={() => setStep(2)}>Continue to Payment</button>
          </div>

          <div style={sectionCardGray}>
            <h2 style={{...sectionHeader, color: '#999'}}>Payment Method</h2>
          </div>
        </div>

        {/* SIDEBAR: Global Order Summary */}
        <div style={sidebar}>
          <div style={summaryCard}>
            <h2 style={summaryTitle}>Order Summary</h2>
            <div style={summaryRow}>
              <span>Items ({cart.length})</span>
              <span>RWF {subtotal.toLocaleString()}</span>
            </div>
            <div style={summaryRow}>
              <span>Shipping to {selectedCountry}</span>
              <span>RWF {delivery.toLocaleString()}</span>
            </div>
            <hr style={divider} />
            <div style={totalRow}>
              <span>Order total</span>
              <span>RWF {total.toLocaleString()}</span>
            </div>
            
            <button style={confirmBtn} onClick={() => setStep(3)}>Confirm and pay</button>
            
            <div style={protectionBox}>
              <ShieldCheck size={20} color="#0053a0" />
              <span>Purchase protected by <b>MegaMart Global Guarantee</b></span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Success step remains the same...
  return (
    <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Your Global Order is Placed!</h1>
        <p>Shipping to {selectedCountry} is being processed.</p>
    </div>
  );
};

// --- STYLES UPDATED FOR GLOBAL LOOK ---
const pageWrapper = { display: 'flex', gap: '40px', padding: '40px 10%', backgroundColor: '#fff', minHeight: '100vh', fontFamily: 'Arial, sans-serif' };
const mainContent = { flex: 2 };
const sidebar = { flex: 1 };
const sectionCard = { borderBottom: '1px solid #eee', paddingBottom: '30px', marginBottom: '30px' };
const sectionCardGray = { opacity: 0.5 };
const sectionHeader = { fontSize: '24px', fontWeight: '600', marginBottom: '20px', display: 'flex', alignItems: 'center' };
const formRow = { display: 'flex', gap: '12px', marginBottom: '15px' };
const inputGroupFull = { display: 'flex', flexDirection: 'column', width: '100%' };
const label = { fontSize: '13px', color: '#707070', marginBottom: '5px' };
const selectInput = { padding: '15px', border: '1px solid #cbcbcb', borderRadius: '8px', width: '100%', backgroundColor: '#fff', appearance: 'none' };
const inputFull = { width: '100%', padding: '15px', borderRadius: '8px', border: '1px solid #cbcbcb' };
const inputHalf = { width: '50%', padding: '15px', borderRadius: '8px', border: '1px solid #cbcbcb' };
const inputThird = { width: '33.3%', padding: '15px', borderRadius: '8px', border: '1px solid #cbcbcb' };
const phoneWrapper = { width: '100%', border: '1px solid #cbcbcb', borderRadius: '8px' };
const phoneInput = { width: '100%', border: 'none', padding: '15px', borderRadius: '8px', outline: 'none' };
const helperText = { fontSize: '12px', color: '#707070', marginTop: '-10px' };
const doneBtn = { backgroundColor: '#005efd', color: 'white', padding: '15px 40px', borderRadius: '25px', border: 'none', fontWeight: 'bold', cursor: 'pointer' };
const summaryCard = { padding: '20px', border: '1px solid #eee', borderRadius: '12px', position: 'sticky', top: '20px' };
const summaryTitle = { fontSize: '22px', marginTop: 0 };
const summaryRow = { display: 'flex', justifyContent: 'space-between', margin: '15px 0', fontSize: '14px' };
const totalRow = { display: 'flex', justifyContent: 'space-between', fontWeight: 'bold', fontSize: '18px', margin: '20px 0' };
const divider = { border: 'none', borderTop: '1px solid #eee' };
const confirmBtn = { width: '100%', padding: '15px', backgroundColor: '#005efd', color: 'white', border: 'none', borderRadius: '25px', fontWeight: 'bold' };
const protectionBox = { display: 'flex', gap: '10px', marginTop: '20px', fontSize: '13px', color: '#333' };
const successContainer = { textAlign: 'center', padding: '100px' };

export default CheckoutPage;
