import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ShieldCheck, ChevronDown, CheckCircle, 
  Star, Globe, CreditCard, Wallet, Smartphone 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [rating, setRating] = useState(0);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');

  const currencies = [
    { code: 'RWF', symbol: 'RWF', rate: 1 },
    { code: 'USD', symbol: '$', rate: 0.00078 },
    { code: 'EUR', symbol: '€', rate: 0.00072 }
  ];

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const deliveryBase = selectedCountry === 'Rwanda' ? 2000 : 15000;

  const formatPrice = (amount) => {
    const converted = (amount * currency.rate).toLocaleString(undefined, {
      minimumFractionDigits: currency.code === 'RWF' ? 0 : 2,
      maximumFractionDigits: currency.code === 'RWF' ? 0 : 2
    });
    return `${currency.symbol} ${converted}`;
  };

  if (step === 3) {
    return (
      <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Success!</h1>
        <p>Your order is shipping to {selectedCountry}.</p>
        <button onClick={() => navigate('/')} style={continueBtn}>Return Home</button>
      </div>
    );
  }

  return (
    <div style={pageWrapper}>
      <div style={mainContent}>
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={20} /> 1. Shipping Information</h2>
          <div style={formRow}>
            <select style={selectInput} value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
              <option>Rwanda</option>
              <option>United States</option>
              <option>Other Global</option>
            </select>
          </div>
          <div style={formRow}>
            <input style={inputHalf} placeholder="First name" />
            <input style={inputHalf} placeholder="Last name" />
          </div>
          <input style={inputFull} placeholder="Street address" />
        </div>

        <div style={sectionCard}>
          <h2 style={sectionHeader}><CreditCard size={20} /> 2. Payment & Currency</h2>
          <div style={paymentGrid}>
            <div style={paymentMethod === 'card' ? activePayment : paymentOption} onClick={() => setPaymentMethod('card')}>
              <CreditCard size={18} /> Visa / MasterCard
            </div>
            <div style={paymentMethod === 'paypal' ? activePayment : paymentOption} onClick={() => setPaymentMethod('paypal')}>
              <Wallet size={18} /> PayPal
            </div>
          </div>
        </div>
      </div>

      <div style={sidebar}>
        <div style={summaryCard}>
          <h3>Order Summary</h3>
          <div style={summaryRow}><span>Items</span><span>{formatPrice(subtotal)}</span></div>
          <div style={summaryRow}><span>Shipping</span><span>{formatPrice(deliveryBase)}</span></div>
          <hr />
          <div style={totalRow}><span>Total</span><span>{formatPrice(subtotal + deliveryBase)}</span></div>
          <button style={confirmBtn} onClick={() => setStep(3)}>Confirm & Pay</button>
        </div>
      </div>
    </div>
  );
};

// Fixed Style Object (Ensuring all keys used above are defined)
const pageWrapper = { display: 'flex', gap: '30px', padding: '40px 10%', backgroundColor: '#f8f9fa' };
const mainContent = { flex: 2 };
const sidebar = { flex: 1 };
const sectionCard = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', marginBottom: '20px', border: '1px solid #eee' };
const sectionHeader = { fontSize: '20px', display: 'flex', alignItems: 'center', gap: '10px' };
const formRow = { display: 'flex', gap: '10px', marginBottom: '10px' };
const selectInput = { width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd' };
const inputFull = { width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd' };
const inputHalf = { width: '50%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd' };
const paymentGrid = { display: 'flex', flexDirection: 'column', gap: '10px' };
const paymentOption = { display: 'flex', alignItems: 'center', gap: '10px', padding: '12px', border: '1px solid #ddd', borderRadius: '8px', cursor: 'pointer' };
const activePayment = { ...paymentOption, borderColor: '#005efd', backgroundColor: '#f0faff' };
const summaryCard = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #eee', position: 'sticky', top: '20px' };
const summaryRow = { display: 'flex', justifyContent: 'space-between', marginBottom: '10px' };
const totalRow = { display: 'flex', justifyContent: 'space-between', fontWeight: 'bold', fontSize: '18px' };
const confirmBtn = { width: '100%', padding: '15px', backgroundColor: '#005efd', color: 'white', border: 'none', borderRadius: '30px', fontWeight: 'bold', marginTop: '20px', cursor: 'pointer' };
const successContainer = { textAlign: 'center', padding: '100px' };
const continueBtn = { marginTop: '20px', padding: '10px 20px', backgroundColor: '#333', color: 'white', border: 'none', borderRadius: '8px' };

export default CheckoutPage;
