import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  CreditCard, ShieldCheck, MapPin, Smartphone, 
  CheckCircle, Star, ArrowRight, Trash2 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [checkoutStep, setCheckoutStep] = useState(2); 
  const [loading, setLoading] = useState(false);
  const [rating, setRating] = useState(0);
  const [shippingDetails, setShippingDetails] = useState({ phone: '', address: '', district: 'Gasabo' });

  const subtotal = cart.reduce((sum, item) => sum + (item.price || 0), 0);
  const deliveryFee = 2000; //
  const total = subtotal + deliveryFee;

  const handleFinishPayment = async () => {
    setLoading(true);
    try {
      const { error } = await supabase.from('orders').insert([{
        user_id: user?.id,
        items: cart,
        total_amount: total,
        phone_number: shippingDetails.phone,
        status: 'paid'
      }]);
      if (error) throw error;
      setCart([]);
      setCheckoutStep(4); 
    } catch (err) {
      alert("Session expired. Please refresh."); // Fix for
    } finally {
      setLoading(false);
    }
  };

  // SUCCESS SCREEN (Step 4 & 5)
  if (checkoutStep === 4) return (
    <div style={successContainer}>
      <CheckCircle size={80} color="#28a745" />
      <h1>Thanks for buying on MegaMart!</h1>
      <p>We've received your order for {shippingDetails.district}.</p>
      <div style={ratingBox}>
        <h3>Rate our website</h3>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
          {[1, 2, 3, 4, 5].map(star => (
            <Star key={star} fill={rating >= star ? "#ffc107" : "none"} color="#ffc107" style={{ cursor: 'pointer' }} onClick={() => setRating(star)} />
          ))}
        </div>
        {rating > 0 && <p style={{ color: '#28a745', marginTop: '10px' }}>Thank you!</p>}
        <button onClick={() => navigate('/')} style={continueBtn}>Return to Shop</button>
      </div>
    </div>
  );

  return (
    <div style={pageLayout}>
      {/* LEFT: INFORMATION & PAYMENT */}
      <div style={leftColumn}>
        <h2 style={sectionHeader}>Secure Checkout</h2>
        
        {/* Step 2: Delivery Details */}
        <section style={cardStyle}>
          <h3 style={cardTitle}><MapPin size={20} color="#008ECC" /> Delivery Destination</h3>
          <div style={formGrid}>
            <div style={inputGroup}>
              <label style={labelStyle}>Phone Number (Momo)</label>
              <input style={inputStyle} placeholder="078... / 079..." onChange={e => setShippingDetails({...shippingDetails, phone: e.target.value})} />
            </div>
            <div style={inputGroup}>
              <label style={labelStyle}>District</label>
              <select style={inputStyle} onChange={e => setShippingDetails({...shippingDetails, district: e.target.value})}>
                <option>Gasabo</option><option>Kicukiro</option><option>Nyarugenge</option>
              </select>
            </div>
            <div style={{...inputGroup, gridColumn: 'span 2'}}>
              <label style={labelStyle}>Specific Street / Landmark</label>
              <input style={inputStyle} placeholder="Example: KG 123 St, near the stadium" onChange={e => setShippingDetails({...shippingDetails, address: e.target.value})} />
            </div>
          </div>
        </section>

        {/* Step 3: Payment Method */}
        <section style={cardStyle}>
          <h3 style={cardTitle}><CreditCard size={20} color="#008ECC" /> Payment Method</h3>
          <div style={activeMethod}>
            <Smartphone size={22} />
            <div>
              <p style={methodName}>Mobile Money</p>
              <p style={methodDesc}>MTN MoMo or Airtel Money</p>
            </div>
          </div>
        </section>
      </div>

      {/* RIGHT: ORDER SUMMARY */}
      <div style={rightColumn}>
        <div style={summaryCard}>
          <h3 style={{ marginTop: 0 }}>Order Summary</h3>
          <div style={itemList}>
            {cart.map((item, index) => (
              <div key={index} style={itemSummaryRow}>
                <p style={itemName}>{item.name} ({item.selectedVariant})</p>
                <p style={itemPrice}>RWF {item.price?.toLocaleString()}</p>
              </div>
            ))}
          </div>
          <hr style={divider} />
          <div style={summaryRow}><span>Subtotal</span><span>RWF {subtotal.toLocaleString()}</span></div>
          <div style={summaryRow}><span>Delivery</span><span>RWF {deliveryFee.toLocaleString()}</span></div>
          <div style={totalRow}><span>Total</span><span>RWF {total.toLocaleString()}</span></div>
          
          <button onClick={handleFinishPayment} style={orderBtn} disabled={loading || !shippingDetails.phone}>
            {loading ? "Processing..." : "Place Order Now"}
          </button>
          
          <div style={securityNote}><ShieldCheck size={14} /> 100% Secure Transaction</div>
        </div>
      </div>
    </div>
  );
};

// Styles from
const pageLayout = { display: 'flex', gap: '30px', padding: '40px 8%', backgroundColor: '#f8f9fa', minHeight: '100vh' };
const leftColumn = { flex: 2, display: 'flex', flexDirection: 'column', gap: '20px' };
const rightColumn = { flex: 1.2 };
const sectionHeader = { fontSize: '28px', margin: 0 };
const cardStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '16px', border: '1px solid #eee' };
const cardTitle = { display: 'flex', alignItems: 'center', gap: '10px', marginTop: 0 };
