import React from 'react';
import { ShieldCheck, Lock, EyeOff, Bell } from 'lucide-react';

const PrivacyPolicy = () => {
  const BRAND_BLUE = '#008ECC';

  return (
    <div style={container}>
      <section style={header}>
        <h1 style={title}>Privacy Policy</h1>
        <p style={subtitle}>How MegaMart Rwanda protects your personal data.</p>
      </section>

      <div style={content}>
        <section style={section}>
          <div style={iconHeader}><Lock color={BRAND_BLUE} /> <h2>1. Data Collection</h2></div>
          <p style={text}>
            We collect information you provide directly to us when you create an account, make a purchase, 
            or communicate with our support team. This includes your name, email address, phone number 
            (for MTN MoMo/Airtel Money), and delivery address.
          </p>
        </section>

        <section style={section}>
          <div style={iconHeader}><ShieldCheck color={BRAND_BLUE} /> <h2>2. How We Use Your Data</h2></div>
          <p style={text}>
            Your data is used specifically to process orders, send digital receipts via our automated 
            email system, and provide delivery updates within Kigali and other provinces.
          </p>
        </section>

        <section style={section}>
          <div style={iconHeader}><EyeOff color={BRAND_BLUE} /> <h2>3. Data Sharing</h2></div>
          <p style={text}>
            MegaMart does not sell your data. We only share necessary information with trusted partners:
            <b> logistics providers</b> for delivery and <b>payment processors</b> for MoMo transactions.
          </p>
        </section>
      </div>
    </div>
  );
};

// Styles
const container = { backgroundColor: '#fff', minHeight: '100vh', paddingBottom: '60px' };
const header = { backgroundColor: '#131921', color: '#fff', padding: '60px 20px', textAlign: 'center' };
const title = { fontSize: '32px', margin: '0 0 10px 0' };
const subtitle = { opacity: 0.8 };
const content = { maxWidth: '800px', margin: '0 auto', padding: '40px 20px' };
const section = { marginBottom: '40px' };
const iconHeader = { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '15px' };
const text = { color: '#555', lineHeight: '1.7' };

export default PrivacyPolicy;
