import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { 
  Package, Truck, CheckCircle, Tag, LogOut, 
  RefreshCcw, Trash2, Search, DollarSign, 
  ShoppingCart, Copy, ExternalLink 
} from 'lucide-react';

const AdminDashboard = ({ setIsAdmin }) => {
  const [activeTab, setActiveTab] = useState('orders'); 
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  
  const [formData, setFormData] = useState({ 
    name: '', 
    price: '', 
    category: 'Smartphones', 
    description: '' 
  });
  
  const [totalSales, setTotalSales] = useState(0);

  useEffect(() => {
    fetchInventory();
    fetchOrders();
  }, []);

  const fetchInventory = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (error) console.error("Inventory Fetch Error:", error);
    if (data) setProducts(data);
  };

  const fetchOrders = async () => {
    setLoading(true);
    const { data, error } = await supabase.from('orders').select('*').order('created_at', { ascending: false });
    if (error) console.error("Orders Fetch Error:", error);
    if (data) {
      setOrders(data);
      const total = data.reduce((sum, order) => sum + (order.total_amount || 0), 0);
      setTotalSales(total);
    }
    setLoading(false);
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    const { error } = await supabase.from('orders').update({ status: newStatus }).eq('id', orderId);
    if (!error) {
      alert(`Order updated to ${newStatus}`);
      fetchOrders();
    }
  };

  const copyToClipboard = (id) => {
    navigator.clipboard.writeText(id);
    alert("Order ID copied! Send this to the customer for tracking.");
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const productToInsert = { 
      name: formData.name,
      price: parseFloat(formData.price),
      category: formData.category,
      description: formData.description || 'No description provided'
    };

    const { error } = await supabase.from('products').insert([productToInsert]);

    if (error) {
      alert(`Failed: ${error.message}`);
    } else {
      alert("Product Registered Successfully!");
      setFormData({ name: '', price: '', category: 'Smartphones', description: '' });
      fetchInventory();
    }
  };

  const deleteProduct = async (id, name) => {
    if (window.confirm(`Are you sure you want to delete "${name}"?`)) {
      const { error } = await supabase.from('products').delete().eq('id', id);
      if (!error) {
        fetchInventory();
      }
    }
  };

  const filteredProducts = products.filter(p => 
    p.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.category?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={containerStyle} className="admin-container">
      {/* HEADER */}
      <div style={headerStyle} className="admin-header">
        <div>
          <h1 style={{ margin: 0, fontSize: '22px' }}>MegaMart Command Center</h1>
          <p style={{ color: '#666', fontSize: '13px' }}>Rwanda Operations</p>
        </div>
        <button onClick={() => setIsAdmin(false)} style={logoutBtn}><LogOut size={16} /> Logout</button>
      </div>

      {/* STATS */}
      <div style={statsContainer} className="admin-stats-grid">
        <div style={statCard}>
          <div style={statIconBox}><DollarSign size={20} color="#28a745" /></div>
          <div>
            <p style={statLabel}>REVENUE</p>
            <h2 style={statValue}>RWF {totalSales.toLocaleString()}</h2>
          </div>
        </div>
        <div style={{...statCard, display: 'none'}} className="desktop-stat"> {/* Example of hiding on small screens if needed */}
           <div style={{...statIconBox, backgroundColor: '#e7f3ff'}}><ShoppingCart size={20} color="#007bff" /></div>
           <div><p style={statLabel}>ORDERS</p><h2 style={statValue}>{orders.length}</h2></div>
        </div>
      </div>

      {/* TABS */}
      <div style={tabBar}>
        <button onClick={() => setActiveTab('orders')} style={activeTab === 'orders' ? activeTabBtn : tabBtn}>
          <Package size={18} /> Orders
        </button>
        <button onClick={() => setActiveTab('inventory')} style={activeTab === 'inventory' ? activeTabBtn : tabBtn}>
          <Tag size={18} /> Inventory
        </button>
      </div>

      {activeTab === 'orders' ? (
        <div style={panelStyle}>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
            <h3 style={{margin:0}}>Recent Sales</h3>
            <button onClick={fetchOrders} style={refreshBtn}><RefreshCcw size={18} /></button>
          </div>
          <div style={{ overflowX: 'auto' }}>
            <table style={tableStyle}>
              <thead>
                <tr style={thRow}>
                  <th>ID / DATE</th>
                  <th>CUSTOMER</th>
                  <th>TOTAL</th>
                  <th>STATUS</th>
                  <th>ACTION</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(o => (
                  <tr key={o.id} style={trStyle}>
                    <td style={{ padding: '12px 0' }}>
                      <div style={{fontSize: '10px', color: '#888'}}>{o.id.slice(0,8)}</div>
                      <div style={{fontSize: '11px'}}>{new Date(o.created_at).toLocaleDateString()}</div>
                    </td>
                    <td style={{fontSize: '12px'}}>{o.user_email || 'Guest'}</td>
                    <td style={{fontWeight: 'bold'}}>RWF {o.total_amount?.toLocaleString()}</td>
                    <td><span style={getStatusBadge(o.status)}>{o.status}</span></td>
                    <td>
                      <div style={{ display: 'flex', gap: '8px' }}>
                        <button onClick={() => updateOrderStatus(o.id, 'shipped')} style={actionBtn} title="Ship"><Truck size={14} /></button>
                        <button onClick={() => copyToClipboard(o.id)} style={{...actionBtn, backgroundColor: '#6c757d'}} title="Copy ID"><Copy size={14} /></button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="inventory-grid" style={inventoryGridStyle}>
          <div style={panelStyle}>
            <h3>Add Product</h3>
            <form onSubmit={handleAddProduct} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              <input type="text" placeholder="Name" style={inputStyle} onChange={e => setFormData({...formData, name: e.target.value})} value={formData.name} required />
              <input type="number" placeholder="Price" style={inputStyle} onChange={e => setFormData({...formData, price: e.target.value})} value={formData.price} required />
              <input type="text" placeholder="Description" style={inputStyle} onChange={e => setFormData({...formData, description: e.target.value})} value={formData.description} />
              <select style={inputStyle} value={formData.category} onChange={e => setFormData({...formData, category: e.target.value})}>
                <option>Smartphones</option><option>Laptops</option><option>Accessories</option>
              </select>
              <button type="submit" style={mainBtn}>Add to Shop</button>
            </form>
          </div>

          <div style={panelStyle}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }} className="mobile-column">
              <h3 style={{margin:0}}>Stock</h3>
              <div style={searchContainer}>
                <Search size={14} color="#888" />
                <input type="text" placeholder="Search..." style={searchInput} value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              </div>
            </div>
            <div style={{ overflowX: 'auto' }}>
              <table style={tableStyle}>
                <tbody>
                  {filteredProducts.map(p => (
                    <tr key={p.id} style={trStyle}>
                      <td style={{ padding: '10px 0' }}>
                        <div style={{fontWeight:'bold'}}>{p.name}</div>
                        <div style={{fontSize:'10px', color: '#888'}}>{p.category}</div>
                      </td>
                      <td style={{textAlign: 'right'}}>
                        <div style={{fontSize: '12px'}}>RWF {p.price?.toLocaleString()}</div>
                        <button onClick={() => deleteProduct(p.id, p.name)} style={deleteBtn}><Trash2 size={12} /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- STYLES (Fixed React Warnings & Added Mobile Logic) ---
