import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const Sidebar = ({ isOpen, onClose }) => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      // Fetch unique categories from your products table
      const { data, error } = await supabase
        .from('products')
        .select('category')
        // This trick gets unique values
        .not('category', 'is', null);
      
      if (!error) {
        const uniqueCats = [...new Set(data.map(item => item.category))];
        setCategories(uniqueCats);
      }
    };
    fetchCategories();
  }, []);

  return (
    <div style={{ ...sidebarStyles, left: isOpen ? '0' : '-300px' }}>
      <button onClick={onClose} style={closeBtn}>Ã—</button>
      <h3 style={{ padding: '20px' }}>All Categories</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {categories.map((cat) => (
          <li key={cat} style={navItem}>
            <Link to={`/category/${cat}`} onClick={onClose} style={navLink}>
              {cat.charAt(0).toUpperCase() + cat.slice(1)}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};
