import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, ShoppingCart, Menu, User, Headphones } from 'lucide-react';
import './Navbar.css'; 

const Navbar = ({ cartCount, openSidebar, categories }) => {
  const [searchInput, setSearchInput] = useState('');
  const navigate = useNavigate();

  // Your Brand Colors
  const BRAND_BLUE = '#008ECC';

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchInput.trim()) {
      navigate(`/category/search?q=${searchInput}`);
    }
  };

  return (
    <nav style={navContainer}>
      {/* 1. TOP ROW: White background, Blue Logo */}
      <div style={topRow}>
        <div style={logoSection}>
          <Menu 
            size={28} 
            onClick={openSidebar} 
            style={{ cursor: 'pointer', color: '#333' }} 
          />
          <Link to="/" style={{ textDecoration: 'none' }}>
            <div style={logoWrapper}>
               <Headphones size={24} color={BRAND_BLUE} />
               <h1 style={{...logoText, color: BRAND_BLUE}}>MegaMart</h1>
            </div>
          </Link>
        </div>

        {/* SEARCH BAR: Rounded with Blue Border */}
        <form onSubmit={handleSearch} style={searchBarContainer}>
          <input 
            type="text" 
            placeholder="Search for electronics, smartphones..." 
            style={searchInputStyle}
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <button type="submit" style={{...searchBtn, backgroundColor: BRAND_BLUE}}>
            <Search size={20} color="white" />
          </button>
        </form>

        <div style={navLinks}>
          <Link to="/profile" style={navItem}>
            <User size={22} color="#444" />
            <div className="hide-mobile" style={textGroup}>
              <span style={navTinyText}>Sign in</span>
              <span style={navBoldText}>Account</span>
            </div>
          </Link>
          
          <div style={navItem} onClick={() => navigate('/checkout')}>
            <div style={cartIconContainer}>
              <ShoppingCart size={26} color="#444" />
              <span style={{...cartBadge, backgroundColor: BRAND_BLUE}}>{cartCount}</span>
            </div>
            <span style={navBoldText}>Cart</span>
          </div>
        </div>
      </div>

      {/* 2. BOTTOM ROW: Light Blue Background */}
      <div style={{...bottomRow, backgroundColor: BRAND_BLUE}}>
        <div onClick={openSidebar} style={allBtn}>
          <Menu size={18} /> <b>All Categories</b>
        </div>
        <div style={categoryLinks} className="category-links">
          {/* Quick links for the first 6 categories */}
          {categories && categories.slice(0, 6).map((cat) => (
            <Link key={cat} to={`/category/${cat}`} style={catLink}>
              {cat}
            </Link>
          ))}
          <Link to="/about" style={catLink}>About Us</Link>
        </div>
      </div>
    </nav>
  );
};

// --- Updated Styling to match KiKUU Light Theme ---

const navContainer = {
  display: 'flex',
  flexDirection: 'column',
  width: '100%',
  boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
  position: 'sticky',
  top: 0,
  zIndex: 1000
};

const topRow = { 
  display: 'flex', 
  alignItems: 'center', 
  padding: '12px 8%', 
  gap: '20px', 
  justifyContent: 'space-between',
  backgroundColor: '#ffffff' // Clean White
};

const logoSection = { display: 'flex', alignItems: 'center', gap: '15px' };
const logoWrapper = { display: 'flex', alignItems: 'center', gap: '5px' };
const logoText = { fontSize: '24px', fontWeight: '900', margin: 0, letterSpacing: '-0.5px' };

const searchBarContainer = { 
  flex: 1, 
  display: 'flex', 
  maxWidth: '700px', 
  height: '42px', 
  margin: '0 20px',
  border: '2px solid #008ECC', // Blue Border
  borderRadius: '25px', // Fully Rounded
  overflow: 'hidden'
};

const searchInputStyle = { 
  flex: 1, 
  padding: '0 20px', 
  border: 'none', 
  outline: 'none', 
  fontSize: '14px',
  color: '#333'
};

const searchBtn = { 
  border: 'none', 
  width: '60px', 
  cursor: 'pointer', 
  display: 'flex', 
  justifyContent: 'center', 
  alignItems: 'center' 
};

const navLinks = { display: 'flex', gap: '25px', alignItems: 'center' };
const navItem = { 
  display: 'flex', 
  alignItems: 'center', 
  gap: '10px', 
  cursor: 'pointer', 
  textDecoration: 'none', 
  color: '#333' 
};

const textGroup = { display: 'flex', flexDirection: 'column' };
const navTinyText = { fontSize: '11px', color: '#777' };
const navBoldText = { fontSize: '14px', fontWeight: '700', color: '#333' };

const cartIconContainer = { position: 'relative', display: 'flex', alignItems: 'center' };
const cartBadge = { 
  position: 'absolute', 
  top: '-8px', 
  right: '-10px', 
  color: 'white', 
  borderRadius: '12px', 
  padding: '2px 6px', 
  fontSize: '10px', 
  fontWeight: 'bold', 
  minWidth: '18px', 
  textAlign: 'center',
  border: '2px solid white'
};

const bottomRow = { 
  display: 'flex', 
  alignItems: 'center', 
  gap: '30px', 
  padding: '0 8%', 
  height: '45px',
  color: 'white',
  fontSize: '14px' 
};

const allBtn = { 
  display: 'flex', 
  alignItems: 'center', 
  gap: '8px', 
  cursor: 'pointer', 
  whiteSpace: 'nowrap',
  backgroundColor: 'rgba(255,255,255,0.2)',
  padding: '5px 15px',
  borderRadius: '4px'
};

const categoryLinks = { 
  display: 'flex', 
  gap: '20px', 
  overflowX: 'auto', 
  whiteSpace: 'nowrap',
  scrollbarWidth: 'none' 
};

const catLink = { 
  color: 'white', 
  textDecoration: 'none', 
  fontWeight: '500',
  opacity: 0.9,
  transition: 'opacity 0.2s'
};

export default Navbar;
