import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { supabase } from './supabaseClient'; // Ensure this is imported
import HomePage from './pages/HomePage';
import AdminDashboard from './pages/AdminDashboard';
import SuccessPage from './pages/SuccessPage'; 
import Navbar from './components/Navbar';
import CheckoutModal from './components/CheckoutModal';
import LoginPage from './pages/LoginPage';
import AuthPage from './pages/AuthPage'; // New User Auth Page

function App() {
  const [cartCount, setCartCount] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [user, setUser] = useState(null); // Track the logged-in customer

  // Check for active user session on load
  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setUser(session?.user ?? null);
    });

    // Listen for login/logout changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
    });

    return () => subscription.unsubscribe();
  }, []);

  const addToCart = () => setCartCount(prev => prev + 1);

  return (
    <Router>
      <div className="App" style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        
        {/* Hidden Top Bar */}
        <div style={{ backgroundColor: '#212121', color: 'white', padding: '8px 50px', fontSize: '11px', display: 'flex', justifyContent: 'space-between' }}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none', opacity: 0.8 }}>MEGAMART RWANDA - OFFICIAL STORE</Link>
          {user && <span style={{ opacity: 0.8 }}>Logged in as: {user.email}</span>}
        </div>

        {/* Pass user to Navbar so it can show "Account" instead of "Sign Up" */}
        <Navbar cartCount={cartCount} openModal={() => setIsModalOpen(true)} user={user} />
        
        <main style={{ flex: 1 }}>
          <Routes>
            <Route path="/" element={<HomePage addToCart={addToCart} />} />
            
            {/* Customer Auth Route */}
            <Route path="/login" element={<AuthPage />} />

            {/* Admin Routes */}
            <Route path="/admin-login" element={<LoginPage setIsAdmin={setIsAdmin} />} />
            <Route 
              path="/admin" 
              element={isAdmin ? <AdminDashboard setIsAdmin={setIsAdmin} /> : <Navigate to="/admin-login" />} 
            />
            
            <Route path="/success" element={<SuccessPage />} />
          </Routes>
        </main>

        {isModalOpen && <CheckoutModal cartCount={cartCount} closeModal={() => setIsModalOpen(false)} user={user} />}
      </div>
    </Router>
  );
}

export default App;
