import React, { useState } from 'react'; // Add useState here
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import HomePage from './pages/HomePage';
import AdminDashboard from './pages/AdminDashboard';
import SuccessPage from './pages/SuccessPage'; 
import Navbar from './components/Navbar';

function App() {
  const [cartCount, setCartCount] = useState(0); // Track the number of items

  const addToCart = () => {
    setCartCount(prev => prev + 1);
  };

  return (
    <Router>
      <div className="App" style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        
        <div style={{ backgroundColor: '#212121', color: 'white', padding: '8px 50px', fontSize: '11px', display: 'flex', gap: '20px' }}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>CUSTOMER VIEW</Link>
          <span style={{ opacity: 0.3 }}>|</span>
          <Link to="/admin" style={{ color: 'white', textDecoration: 'none' }}>ADMIN PANEL</Link>
        </div>

        {/* Pass the cartCount to the Navbar */}
        <Navbar cartCount={cartCount} />
        
        <main style={{ flex: 1 }}>
          <Routes>
            {/* Pass the addToCart function to the HomePage */}
            <Route path="/" element={<HomePage addToCart={addToCart} />} />
            <Route path="/admin" element={<AdminDashboard />} />
            <Route path="/success" element={<SuccessPage />} />
          </Routes>
        </main>

        <footer style={{ backgroundColor: '#fff', padding: '30px', textAlign: 'center', borderTop: '1px solid #eee', marginTop: '50px' }}>
          <p style={{ color: '#008ECC', fontWeight: 'bold' }}>MegaMart Rwanda</p>
          <p style={{ fontSize: '12px', color: '#888' }}>Â© 2025 Electronics Store Demo.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
