import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { ShoppingCart, CreditCard, ArrowLeft, Check } from 'lucide-react';

const ProductDetailPage = ({ addToCart }) => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState('Black'); // Step 1: Default style
  const [loading, setLoading] = useState(true);

  const colors = ['White', 'Pink', 'Blue', 'Black']; // Styles from KiKuu

  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      const { data, error } = await supabase.from('products').select('*').eq('id', productId).single();
      if (!error) setProduct(data);
      setLoading(false);
    };
    fetchProduct();
  }, [productId]);

  const handleBuyNow = (product) => {
    // Pass product + the chosen style (color)
    addToCart({ ...product, selectedVariant: selectedColor });
    navigate('/checkout'); 
  };

  if (loading) return <div style={centerStyle}>Loading Gadget Details...</div>;
  if (!product) return <div style={centerStyle}>Product not found.</div>;

  return (
    <div style={containerStyle}>
      <button onClick={() => navigate(-1)} style={backBtn}><ArrowLeft size={18} /> Back to Shop</button>

      <div style={contentGrid}>
        <div style={imageWrapper}>
          <img src={product.description} alt={product.name} style={imageStyle} />
        </div>

        <div style={detailsStyle}>
          <span style={categoryLabel}>{product.category}</span>
          <h1 style={titleStyle}>{product.name}</h1>
          
          <div style={priceContainer}>
            <p style={priceStyle}>RWF {product.price?.toLocaleString()}</p>
          </div>

          {/* STEP 1: CHOOSE STYLE */}
          <div style={styleSelector}>
            <p style={labelStyle}>Select Color:</p>
            <div style={{ display: 'flex', gap: '10px' }}>
              {colors.map(color => (
                <button 
                  key={color}
                  onClick={() => setSelectedColor(color)}
                  style={{
                    ...colorBtn,
                    border: selectedColor === color ? '2px solid #008ECC' : '1px solid #ddd',
                    backgroundColor: selectedColor === color ? '#f0faff' : 'white'
                  }}
                >
                  {selectedColor === color && <Check size={14} color="#008ECC" />}
                  {color}
                </button>
              ))}
            </div>
          </div>

          <div style={buttonGroup}>
            <button onClick={() => addToCart({...product, selectedVariant: selectedColor})} style={cartBtn}>
              <ShoppingCart size={18} /> Add to Cart
            </button>
            <button onClick={() => handleBuyNow(product)} style={buyBtn}>
              <CreditCard size={18} /> Buy Now & Pay
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Add these to your existing styles
const styleSelector = { margin: '25px 0', padding: '15px', backgroundColor: '#f9f9f9', borderRadius: '12px' };
const colorBtn = { padding: '8px 16px', borderRadius: '20px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px', fontWeight: '500' };
const centerStyle = { padding: '100px', textAlign: 'center' };
const containerStyle = { padding: '40px 20px', maxWidth: '1100px', margin: 'auto' };
const contentGrid = { display: 'flex', flexWrap: 'wrap', gap: '50px' };
const imageWrapper = { flex: '1', minWidth: '300px' };
const imageStyle = { width: '100%', borderRadius: '15px' };
const detailsStyle = { flex: '1.2' };
const categoryLabel = { color: '#008ECC', fontWeight: 'bold' };
const titleStyle = { fontSize: '32px' };
const priceContainer = { marginBottom: '20px' };
const priceStyle = { color: '#ff4d4f', fontSize: '32px', fontWeight: 'bold' };
const labelStyle = { fontWeight: 'bold', marginBottom: '10px' };
const backBtn = { border: 'none', background: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '20px' };
const buttonGroup = { display: 'flex', flexDirection: 'column', gap: '12px' };
const cartBtn = { padding: '16px', borderRadius: '8px', cursor: 'pointer', display: 'flex', justifyContent: 'center', gap: '10px' };
const buyBtn = { backgroundColor: '#008ECC', color: 'white', padding: '16px', borderRadius: '8px', cursor: 'pointer', display: 'flex', justifyContent: 'center', gap: '10px' };

export default ProductDetailPage;
