import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [inventoryValue, setInventoryValue] = useState(0); 
  const [formData, setFormData] = useState({ name: '', price: '', category: 'Smartphones', image_url: '' });

  const fetchInventory = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (error) {
      console.error("Error fetching data:", error);
    } else if (data) {
      setProducts(data);
      
      // Calculate Total Inventory Value automatically
      const total = data.reduce((sum, item) => sum + (Number(item.price) || 0), 0);
      setInventoryValue(total);
    }
  };

  useEffect(() => { 
    fetchInventory();
  }, []);

  const applyDiscount = async (percentage) => {
    const confirmAction = window.confirm(`Apply a ${percentage}% discount to all Smartphones?`);
    if (confirmAction) {
      const { data: smartphones } = await supabase.from('products').select('*').eq('category', 'Smartphones');
      if (!smartphones) return;

      const updates = smartphones.map(product => {
        const newPrice = product.price * ((100 - percentage) / 100);
        return supabase.from('products').update({ price: newPrice }).eq('id', product.id);
      });

      await Promise.all(updates);
      alert(`Success! Inventory prices updated.`);
      fetchInventory(); // This will automatically update the Revenue/Value at the top
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const { error } = await supabase.from('products').insert([{ 
      name: formData.name, 
      price: parseFloat(formData.price), 
      category: formData.category, 
      image_url: formData.image_url 
    }]);

    if (!error) {
      alert("Product Registered in System");
      setFormData({ name: '', price: '', category: 'Smartphones', image_url: '' });
      fetchInventory();
    }
  };

  return (
    <div style={{ padding: '40px', backgroundColor: '#f4f7f6', minHeight: '100vh', fontFamily: 'sans-serif' }}>
      
      {/* Statistics Header */}
      <div style={{ display: 'flex', gap: '20px', marginBottom: '30px' }}>
        <div style={statCardStyle}>
          <span style={{ fontSize: '11px', color: '#888', fontWeight: 'bold', letterSpacing: '1px' }}>TOTAL INVENTORY VALUE</span>
          <h2 style={{ color: '#1a1a1a', margin: '5px 0' }}>RWF {inventoryValue.toLocaleString()}</h2>
        </div>
        <div style={statCardStyle}>
          <span style={{ fontSize: '11px', color: '#888', fontWeight: 'bold', letterSpacing: '1px' }}>ITEMS IN STOCK</span>
          <h2 style={{ color: '#008ECC', margin: '5px 0' }}>{products.length} Units</h2>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '30px' }}>
        {/* Management Tools */}
        <div style={panelStyle}>
          <h3 style={{ fontSize: '18px', marginBottom: '20px' }}>Product Registration</h3>
          <form onSubmit={handleAddProduct} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            <input type="text" placeholder="Product Name" required style={inputStyle} onChange={(e) => setFormData({...formData, name: e.target.value})} value={formData.name} />
            <input type="number" placeholder="Unit Price (RWF)" required style={inputStyle} onChange={(e) => setFormData({...formData, price: e.target.value})} value={formData.price} />
            <select style={inputStyle} value={formData.category} onChange={(e) => setFormData({...formData, category: e.target.value})}>
              <option>Smartphones</option>
              <option>Laptops</option>
              <option>Accessories</option>
            </select>
            <button type="submit" style={btnStyle}>Register Product</button>
          </form>

          <div style={{ marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
            <h4 style={{ color: '#666', fontSize: '14px', marginBottom: '15px' }}>Price Control Controls</h4>
            <div style={{ display: 'flex', gap: '10px' }}>
                <button onClick={() => applyDiscount(10)} style={{ ...btnStyle, backgroundColor: '#FF8A00', fontSize: '12px' }}>Apply 10% Sale</button>
                <button onClick={() => applyDiscount(20)} style={{ ...btnStyle, backgroundColor: '#E03C3C', fontSize: '12px' }}>Apply 20% Sale</button>
            </div>
          </div>
        </div>

        {/* Inventory View */}
        <div style={panelStyle}>
          <h3 style={{ fontSize: '18px', marginBottom: '20px' }}>Real-time Inventory</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ textAlign: 'left', borderBottom: '2px solid #eee', color: '#888', fontSize: '12px' }}>
                <th style={{ padding: '10px' }}>PRODUCT NAME</th>
                <th>CATEGORY</th>
                <th>UNIT PRICE</th>
              </tr>
            </thead>
            <tbody>
              {products.map(p => (
                <tr key={p.id} style={{ borderBottom: '1px solid #f9f9f9', fontSize: '14px' }}>
                  <td style={{ padding: '12px 10px' }}>{p.name}</td>
                  <td style={{ color: '#666' }}>{p.category}</td>
                  <td style={{ fontWeight: 'bold' }}>RWF {p.price?.toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const statCardStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '15px', flex: 1, boxShadow: '0 4px 12px rgba(0,0,0,0.03)', border: '1px solid #eee' };
const panelStyle = { backgroundColor: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 20px rgba(0,0,0,0.05)' };
const inputStyle = { padding: '12px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px', outline: 'none' };
const btnStyle = { width: '100%', backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '12px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' };

export default AdminDashboard;
