import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ShieldCheck, CheckCircle, Globe, CreditCard, 
  Smartphone, Tag, ChevronRight, Lock, Mail, MapPin, X
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [showMomoOverlay, setShowMomoOverlay] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('momo');

  const [formData, setFormData] = useState({
    email: user?.email || '',
    firstName: '',
    lastName: '',
    address: '',
    phone: ''
  });

  // --- Calculations ---
  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;
  const totalInRWF = subtotal + delivery;

  const formatPrice = (amount) => {
    return `RWF ${amount.toLocaleString()}`;
  };

  // --- Real Payment Simulation (MoMo / Card) ---
  const handleInitiatePayment = () => {
    if (!formData.firstName || !formData.address || !formData.phone) {
      alert("Please fill in all shipping details and phone number.");
      return;
    }

    if (paymentMethod === 'momo') {
      setShowMomoOverlay(true); // Trigger USSD popup
    } else {
      processOrder(); // Direct for card
    }
  };

  const processOrder = async () => {
    setLoading(true);
    try {
      const { error } = await supabase.from('orders').insert([{
        user_email: formData.email || 'Guest',
        items: cart,
        total_amount: totalInRWF,
        shipping_address: `${formData.firstName}, ${formData.address}, ${formData.phone}`,
        status: 'Paid',
        payment_method: paymentMethod
      }]);

      if (error) throw error;
      setCart([]);
      navigate('/success'); 
    } catch (err) {
      alert("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={pageOverlay}>
      {/* 1. REAL PAYMENT OVERLAY (MOMO USSD) */}
      {showMomoOverlay && (
        <div style={momoModalOverlay}>
          <div style={momoCard}>
            <div style={momoHeader}>
              <Smartphone color="#ffcc00" size={32} />
              <h3>Confirm Payment</h3>
            </div>
            <p style={momoText}>Please dial the code below on your phone to authorize the payment of <b>{formatPrice(totalInRWF)}</b></p>
            <div style={ussdCode}>*182*8*1*000000#</div>
            <p style={momoSubtext}>Once you enter your PIN on your phone, click "I have Paid" below.</p>
            <button style={paidBtn} onClick={processOrder} disabled={loading}>
              {loading ? "Verifying..." : "I have Paid"}
            </button>
            <button style={cancelBtn} onClick={() => setShowMomoOverlay(false)}>Cancel</button>
          </div>
        </div>
      )}

      {/* 2. MAIN CHECKOUT CARD */}
      <div style={checkoutCard}>
        {/* LEFT PANEL: INPUTS */}
        <div style={leftPanel}>
          <div style={brandArea}>
            <h2 style={brandName}>MegaMart</h2>
            <div style={stepper}>
              <span style={activeStep}>Shipping</span> <ChevronRight size={14} />
              <span>Delivery</span> <ChevronRight size={14} />
              <span>Payment</span>
            </div>
          </div>

          <div style={formSection}>
            <h4 style={sectionLabel}><Mail size={16}/> Contact Information</h4>
            <input style={fullInput} placeholder="Email" value={formData.email} onChange={e => setFormData({...formData, email: e.target.value})} />
            <input style={fullInput} placeholder="Phone Number (for MoMo)" onChange={e => setFormData({...formData, phone: e.target.value})} />

            <h4 style={sectionLabel}><MapPin size={16}/> Shipping Address</h4>
            <div style={row}>
              <input style={halfInput} placeholder="First name" onChange={e => setFormData({...formData, firstName: e.target.value})} />
              <input style={halfInput} placeholder="Last name" />
            </div>
            <input style={fullInput} placeholder="Street Address / House Number" onChange={e => setFormData({...formData, address: e.target.value})} />
            
            <h4 style={sectionLabel}><CreditCard size={16}/> Payment Method</h4>
            <div style={paymentTabs}>
              <div style={paymentMethod === 'momo' ? activeTab : tab} onClick={() => setPaymentMethod('momo')}>
                <Smartphone size={18} /> Mobile Money
              </div>
              <div style={paymentMethod === 'card' ? activeTab : tab} onClick={() => setPaymentMethod('card')}>
                <CreditCard size={18} /> Card
              </div>
            </div>
          </div>

          <button style={mainPayBtn} onClick={handleInitiatePayment}>
            Pay {formatPrice(totalInRWF)}
          </button>
          <div style={footerNote}><Lock size={12}/> Secure 256-bit encrypted payment</div>
        </div>

        {/* RIGHT PANEL: SUMMARY */}
        <div style={rightPanel}>
          <h3 style={{margin: '0 0 20px'}}>Order Summary</h3>
          <div style={cartScroll}>
            {cart.map((item, i) => (
              <div key={i} style={cartItem}>
                <div style={imgPlaceholder}>{item.name[0]}</div>
                <div style={{flex: 1}}>
                  <div style={itemName}>{item.name}</div>
                  <div style={itemQty}>Qty: 1</div>
                </div>
                <div style={itemPrice}>{formatPrice(item.price)}</div>
              </div>
            ))}
          </div>

          <div style={divider} />
          
          <div style={calcLine}><span>Subtotal</span><span>{formatPrice(subtotal)}</span></div>
          <div style={calcLine}><span>Shipping</span><span>{formatPrice(delivery)}</span></div>
          <div style={divider} />
          <div style={totalLine}><span>Total</span><span>{formatPrice(totalInRWF)}</span></div>

          <div style={trustBadge}>
            <ShieldCheck size={18} color="#008ECC" />
            <span>Guaranteed Safe Checkout</span>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- STYLES FOR IDENTICAL LAYOUT ---
const pageOverlay = { backgroundColor: '#0f1111', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px' };
const checkoutCard = { backgroundColor: 'white', width: '100%', maxWidth: '1000px', display: 'flex', flexWrap: 'wrap', borderRadius: '16px', overflow: 'hidden', boxShadow: '0 50px 100px rgba(0,0,0,0.5)' };
const leftPanel = { flex: 1.2, padding: '40px', minWidth: '350px' };
const rightPanel = { flex: 0.8, backgroundColor: '#f9f9f9', padding: '40px', borderLeft: '1px solid #eee', minWidth: '320px' };

