const mainPayBtn = { width: '100%', padding: '20px', backgroundColor: '#111', color: 'white', border: 'none', borderRadius: '12px', fontWeight: 'bold', fontSize: '18px', cursor: 'pointer', marginTop: '30px' };
const footerNote = { textAlign: 'center', fontSize: '11px', color: '#aaa', marginTop: '15px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px' };

