const calcLine = { display: 'flex', justifyContent: 'space-between', fontSize: '14px', color: '#666', marginBottom: '10px' };
const totalLine = { display: 'flex', justifyContent: 'space-between', fontSize: '20px', fontWeight: '900' };
const trustBadge = { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '12px', color: '#888', justifyContent: 'center', marginTop: '30px' };

// --- MoMo MODAL STYLES ---
const momoModalOverlay = { position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.8)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 };
const momoCard = { backgroundColor: 'white', padding: '40px', borderRadius: '20px', maxWidth: '400px', textAlign: 'center', boxShadow: '0 20px 40px rgba(0,0,0,0.3)' };
const momoHeader = { display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '10px', marginBottom: '20px' };
const ussdCode = { backgroundColor: '#f4f4f4', padding: '15px', borderRadius: '10px', fontSize: '22px', fontWeight: '900', letterSpacing: '2px', margin: '20px 0', border: '2px dashed #008ECC' };
const paidBtn = { width: '100%', padding: '15px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '10px' };
const cancelBtn = { background: 'none', border: 'none', color: '#888', cursor: 'pointer' };

export default CheckoutPage;
