import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import { Mail, Lock, KeyRound, ArrowRight, ShieldCheck, Loader2 } from 'lucide-react';

const AuthPage = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [showOtpScreen, setShowOtpScreen] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otpToken, setOtpToken] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Handle Standard Login
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    
    if (error) {
      alert(error.message);
    } else {
      navigate('/');
    }
    setLoading(false);
  };

  // Step 1: Sign Up triggers the {{ .Token }} email
  const handleSignUp = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.auth.signUp({ email, password });
    
    if (error) {
      alert(error.message);
    } else {
      setShowOtpScreen(true);
    }
    setLoading(false);
  };

  // Step 2: Verify the 6-digit code
  const handleVerifyCode = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.auth.verifyOtp({
      email,
      token: otpToken,
      type: 'signup',
    });

    if (error) {
      alert('Invalid code. Please check your email.');
    } else {
      alert('Verification successful!');
      navigate('/');
    }
    setLoading(false);
  };

  if (showOtpScreen) {
    return (
      <div style={container}>
        <div style={authCard}>
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <ShieldCheck size={48} color="#008ECC" />
          </div>
          <h2 style={title}>Enter Verification Code</h2>
          <p style={subtitle}>Check your email <strong>{email}</strong> for the 6-digit code.</p>
          
          <form onSubmit={handleVerifyCode} style={form}>
            <div style={inputWrapper}>
              <KeyRound size={18} color="#888" />
              <input 
                type="text" 
                placeholder="000000" 
                style={input} 
                value={otpToken} 
                onChange={(e) => setOtpToken(e.target.value)}
                maxLength="6"
                required 
              />
            </div>
            <button type="submit" style={submitBtn} disabled={loading}>
              {loading ? <Loader2 className="animate-spin" /> : 'Verify Account'}
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div style={container}>
      <div style={authCard}>
        <h2 style={title}>{isSignUp ? 'Create Account' : 'Welcome Back'}</h2>
        <p style={subtitle}>MegaMart Rwanda - Official Gadget Store</p>
        
        <form onSubmit={isSignUp ? handleSignUp : handleLogin} style={form}>
          <div style={inputGroup}>
            <label style={labelStyle}>Email</label>
            <div style={inputWrapper}>
              <Mail size={18} color="#888" />
              <input 
                type="email" 
                placeholder="example@gmail.com" 
                style={input} 
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required 
              />
            </div>
          </div>

          <div style={inputGroup}>
            <label style={labelStyle}>Password</label>
            <div style={inputWrapper}>
              <Lock size={18} color="#888" />
              <input 
                type="password" 
                placeholder="••••••••" 
                style={input} 
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required 
              />
            </div>
          </div>

          <button type="submit" style={submitBtn} disabled={loading}>
            {loading ? <Loader2 className="animate-spin" /> : (isSignUp ? 'Register' : 'Sign In')} 
            {!loading && <ArrowRight size={18} />}
          </button>
        </form>

        <p style={toggleText}>
          {isSignUp ? 'Already have an account?' : "New to MegaMart?"}
          <span style={toggleLink} onClick={() => setIsSignUp(!isSignUp)}>
            {isSignUp ? ' Login' : ' Create Account'}
          </span>
        </p>
      </div>
    </div>
  );
};

// --- STYLES ---
const container = { display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '85vh', backgroundColor: '#f9f9f9', padding: '20px' };
const authCard = { backgroundColor: '#fff', padding: '40px', borderRadius: '16px', boxShadow: '0 10px 25px rgba(0,0,0,0.05)', width: '100%', maxWidth: '420px' };
const title = { fontSize: '24px', fontWeight: '800', marginBottom: '8px', textAlign: 'center', color: '#1a1a1a' };
const subtitle = { fontSize: '14px', color: '#666', textAlign: 'center', marginBottom: '30px' };
const form = { display: 'flex', flexDirection: 'column', gap: '20px' };
const inputGroup = { display: 'flex', flexDirection: 'column', gap: '8px' };
const labelStyle = { fontSize: '13px', fontWeight: '600', color: '#444' };
const inputWrapper = { display: 'flex', alignItems: 'center', gap: '12px', padding: '12px 16px', border: '1.5px solid #eee', borderRadius: '10px', transition: '0.3s' };
const input = { border: 'none', outline: 'none', width: '100%', fontSize: '15px' };
const submitBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '14px', borderRadius: '10px', cursor: 'pointer', fontWeight: 'bold', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px', fontSize: '16px', marginTop: '10px' };
const toggleText = { textAlign: 'center', marginTop: '24px', fontSize: '14px', color: '#555' };
const toggleLink = { color: '#008ECC', cursor: 'pointer', fontWeight: '800', textDecoration: 'underline' };

export default AuthPage;
