import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { ShoppingBag, ArrowRight, Zap, Star, ShieldCheck } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Your specific brand colors
  const BRAND_BLUE = '#008ECC'; 
  const BRAND_LIGHT_BLUE = '#E6F4FF';

  const categories = [
    { name: 'Smartphones', img: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?auto=format&fit=crop&w=500&q=80' },
    { name: 'Laptops', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=500&q=80' },
    { name: 'Watch', img: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?auto=format&fit=crop&w=500&q=80' },
    { name: 'Audio', img: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?auto=format&fit=crop&w=500&q=80' },
    { name: 'Gaming', img: 'https://images.unsplash.com/photo-1606813902917-0c7e5b5eab7a?auto=format&fit=crop&w=500&q=80' }
  ];

  const techHighlights = [
    "iPhone 16 Pro", "MacBook M3 Max", "Samsung S24 Ultra", 
    "Sony WH-1000XM5", "PlayStation 5 Pro", "Apple Watch Ultra 2"
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data, error } = await supabase.from('products').select('*').limit(8);
    if (!error) setProducts(data);
    setLoading(false);
  };

  return (
    <div style={homeContainer}>
      {/* 1. TOP ANNOUNCEMENT BAR (Light Blue Theme) */}
      <div style={topBar}>
        MEGAMART RWANDA - OFFICIAL GADGET STORE
      </div>

      {/* 2. SUB-NAV TICKER */}
      <div style={tickerWrapper}>
        <div style={slidingTrack}>
          {[...techHighlights, ...techHighlights].map((item, index) => (
            <span key={index} style={tickerItem}>{item}</span>
          ))}
        </div>
      </div>

      {/* 3. HERO SECTION (Clean White/Blue) */}
      <section style={heroSection}>
        <div style={heroOverlay}>
          <div style={newArrivalBadge}>NEW ARRIVALS 2025</div>
          <h1 style={heroTitle}>The Future of <br/> <span style={{color: BRAND_BLUE}}>Gadgets</span> is Here.</h1>
          <p style={heroSubtitle}>Upgrade your lifestyle with the latest tech from MegaMart Rwanda.</p>
          <div style={heroButtons}>
            <button style={primaryBtn} onClick={() => document.getElementById('featured').scrollIntoView({ behavior: 'smooth' })}>
              Shop Now <ArrowRight size={18} />
            </button>
            <button style={secondaryBtn} onClick={() => navigate('/deals')}>
              View Deals
            </button>
          </div>
        </div>
      </section>

      {/* 4. TRUST BAR */}
      <div style={trustBar}>
        <div style={trustItem}><Zap size={18} color={BRAND_BLUE} /> <span>Same Day Delivery</span></div>
        <div style={trustItem}><ShieldCheck size={18} color={BRAND_BLUE} /> <span>Official Warranty</span></div>
        <div style={trustItem}><Star size={18} color={BRAND_BLUE} /> <span>Top Rated Service</span></div>
      </div>

      {/* 5. CATEGORY BUBBLES */}
      <div style={categoryWrapper}>
        {categories.map(cat => (
          <Link key={cat.name} to={`/category/${cat.name}`} style={catLink}>
            <div style={catCircle}>
              <img src={cat.img} alt={cat.name} style={catImg} />
            </div>
            <span style={catLabel}>{cat.name}</span>
          </Link>
        ))}
      </div>

      {/* 6. FEATURED PRODUCTS */}
      <div style={sectionWrapper} id="featured">
        <div style={sectionHeader}>
          <h2 style={{fontSize: '28px', fontWeight: '800'}}>Featured Products</h2>
          <Link to="/category/all" style={{color: BRAND_BLUE, textDecoration: 'none', fontWeight: '700'}}>View All</Link>
        </div>

        {loading ? <div style={{textAlign: 'center', padding: '50px'}}>Loading...</div> : (
          <div style={productGrid}>
            {products.map((product) => (
              <div key={product.id} style={productCard}>
                <div style={imageContainer}>
                  <img src={product.image_url} alt={product.name} style={productImg} />
                </div>
                <div style={cardBody}>
                  <p style={cardCat}>{product.category}</p>
                  <h3 style={cardTitle}>{product.name}</h3>
                  <div style={priceRow}>
                    <span style={cardPrice}>RWF {product.price?.toLocaleString()}</span>
                    <button onClick={() => addToCart(product)} style={addBtn}>+</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <style>
        {`
          @keyframes scrollTicker { 0% { transform: translateX(0); } 100% { transform: translateX(-50%); } }
          body { margin: 0; font-family: 'Inter', sans-serif; overflow-x: hidden; }
        `}
      </style>
    </div>
  );
};

// --- STYLES (Light Blue & White Theme) ---
const homeContainer = { backgroundColor: '#fff', width: '100%' };

const topBar = { 
