import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import { GoogleGenerativeAI } from "@google/generative-ai";
import { 
  Package, Truck, CheckCircle, Tag, LogOut, 
  RefreshCcw, Trash2, Search, DollarSign, 
  ShoppingCart, Copy, ExternalLink, Sparkles 
} from 'lucide-react';

const AdminDashboard = ({ setIsAdmin }) => {
  const [activeTab, setActiveTab] = useState('orders'); 
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isGenerating, setIsGenerating] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  
  const [formData, setFormData] = useState({ 
    name: '', 
    price: '', 
    category: 'Smartphones', 
    description: '' 
  });
  
  const [totalSales, setTotalSales] = useState(0);

  useEffect(() => {
    fetchInventory();
    fetchOrders();
  }, []);

  // --- AI LOGIC: Using Environment Variable Key ---
  const generateProductDetails = async () => {
    if (!formData.name) {
      alert("Please enter a product name first!");
      return;
    }

    setIsGenerating(true);
    try {
      // Pulling the key from your .env file (REACT_APP_ prefix)
      const apiKey = process.env.REACT_APP_GEMINI_API_KEY;
      
      if (!apiKey) {
        throw new Error("Gemini API Key not found in environment variables.");
      }

      const genAI = new GoogleGenerativeAI(apiKey);
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      const prompt = `You are a tech expert at MegaMart Rwanda. Create a professional product listing for: ${formData.name}. 
      1. Write a 2-sentence marketing description. 
      2. List technical specifications (RAM, Battery, Screen, etc.) as bullet points.
      Keep it professional and suitable for an e-commerce site.`;

      const result = await model.generateContent(prompt);
      const response = await result.response;
      const text = response.text();

      setFormData({ ...formData, description: text });
    } catch (error) {
      console.error("AI Error:", error);
      alert("AI Fill Failed: " + error.message);
    } finally {
      setIsGenerating(false);
    }
  };

  const fetchInventory = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (error) console.error("Inventory Fetch Error:", error);
    if (data) setProducts(data);
  };

  const fetchOrders = async () => {
    setLoading(true);
    const { data, error } = await supabase.from('orders').select('*').order('created_at', { ascending: false });
    if (error) console.error("Orders Fetch Error:", error);
    if (data) {
      setOrders(data);
      const total = data.reduce((sum, order) => sum + (order.total_amount || 0), 0);
      setTotalSales(total);
    }
    setLoading(false);
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const productToInsert = { 
      name: formData.name,
      price: parseFloat(formData.price),
      category: formData.category,
      description: formData.description || 'No description provided'
    };

    const { error } = await supabase.from('products').insert([productToInsert]);

    if (error) {
      alert(`Failed: ${error.message}`);
    } else {
      alert("Product Registered Successfully!");
      setFormData({ name: '', price: '', category: 'Smartphones', description: '' });
      fetchInventory();
    }
  };

  const deleteProduct = async (id, name) => {
    if (window.confirm(`Are you sure you want to delete "${name}"?`)) {
      const { error } = await supabase.from('products').delete().eq('id', id);
      if (!error) fetchInventory();
    }
  };

  const getStatusBadge = (s) => ({
    padding: '3px 7px', borderRadius: '10px', fontSize: '9px', fontWeight: 'bold', textTransform: 'uppercase',
    backgroundColor: s === 'pending' ? '#fff3cd' : s === 'shipped' ? '#d1ecf1' : '#d4edda',
    color: s === 'pending' ? '#856404' : s === 'shipped' ? '#0c5460' : '#155724'
  });

  const filteredProducts = products.filter(p => 
    p.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.category?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={containerStyle}>
      <div style={headerStyle}>
        <div>
          <h1 style={{ margin: 0, fontSize: '22px' }}>MegaMart Command Center</h1>
          <p style={{ color: '#666', fontSize: '13px' }}>Rwanda Operations</p>
        </div>
        <button onClick={() => setIsAdmin(false)} style={logoutBtn}><LogOut size={16} /> Logout</button>
      </div>

      <div style={statsContainer}>
        <div style={statCard}>
          <div style={statIconBox}><DollarSign size={20} color="#28a745" /></div>
          <div>
            <p style={statLabel}>REVENUE</p>
            <h2 style={statValue}>RWF {totalSales.toLocaleString()}</h2>
          </div>
        </div>
      </div>

      <div style={tabBar}>
        <button onClick={() => setActiveTab('orders')} style={activeTab === 'orders' ? activeTabBtn : tabBtn}>
          <Package size={18} /> Orders
        </button>
        <button onClick={() => setActiveTab('inventory')} style={activeTab === 'inventory' ? activeTabBtn : tabBtn}>
          <Tag size={18} /> Inventory
        </button>
      </div>

      {activeTab === 'orders' ? (
        <div style={panelStyle}>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
            <h3 style={{margin:0}}>Recent Sales</h3>
            <button onClick={fetchOrders} style={refreshBtn}><RefreshCcw size={18} /></button>
          </div>
          <div style={{ overflowX: 'auto' }}>
            <table style={tableStyle}>
              <thead>
                <tr style={thRow}>
                  <th>ID / DATE</th>
                  <th>CUSTOMER</th>
                  <th>TOTAL</th>
                  <th>STATUS</th>
                  <th>ACTION</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(o => (
                  <tr key={o.id} style={trStyle}>
                    <td style={{ padding: '12px 0' }}>
                      <div style={{fontSize: '10px', color: '#888'}}>{o.id.slice(0,8)}</div>
                      <div style={{fontSize: '11px'}}>{new Date(o.created_at).toLocaleDateString()}</div>
                    </td>
                    <td style={{fontSize: '12px'}}>{o.user_email || 'Guest'}</td>
                    <td style={{fontWeight: 'bold'}}>RWF {o.total_amount?.toLocaleString()}</td>
                    <td><span style={getStatusBadge(o.status)}>{o.status}</span></td>
                    <td>
                      <button onClick={() => alert("Copied ID")} style={actionBtn}><Copy size={14} /></button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div style={inventoryGridStyle}>
          <div style={panelStyle}>
            <h3>Add Product</h3>
            <form onSubmit={handleAddProduct} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              <div style={{ display: 'flex', gap: '8px' }}>
                <input 
                  type="text" 
                  placeholder="Product Name (e.g. iPhone 15)" 
                  style={{ ...inputStyle, flex: 1 }} 
                  onChange={e => setFormData({...formData, name: e.target.value})} 
                  value={formData.name} 
                  required 
                />
                <button 
                  type="button" 
                  onClick={generateProductDetails} 
                  disabled={isGenerating} 
                  style={magicBtn}
                  title="Generate AI Details"
                >
                  {isGenerating ? <RefreshCcw className="animate-spin" size={16} /> : <Sparkles size={16} />}
                </button>
              </div>
              
              <input type="number" placeholder="Price" style={inputStyle} onChange={e => setFormData({...formData, price: e.target.value})} value={formData.price} required />
              
              <textarea 
                placeholder="Product Description (AI will fill this...)" 
                style={{ ...inputStyle, minHeight: '150px', resize: 'vertical', lineHeight: '1.5' }} 
                onChange={e => setFormData({...formData, description: e.target.value})} 
                value={formData.description} 
              />
              
              <select style={inputStyle} value={formData.category} onChange={e => setFormData({...formData, category: e.target.value})}>
                <option>Smartphones</option><option>Laptops</option><option>Accessories</option>
              </select>
              <button type="submit" style={mainBtn} disabled={loading}>
                {loading ? 'Processing...' : 'Add to Shop'}
              </button>
            </form>
          </div>

          <div style={panelStyle}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
              <h3 style={{margin:0}}>Stock</h3>
              <div style={searchContainer}>
                <Search size={14} color="#888" />
                <input type="text" placeholder="Search..." style={searchInput} value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              </div>
            </div>
            <div style={{ overflowX: 'auto' }}>
              <table style={tableStyle}>
                <tbody>
                  {filteredProducts.map(p => (
                    <tr key={p.id} style={trStyle}>
                      <td style={{ padding: '10px 0' }}>
                        <div style={{fontWeight:'bold'}}>{p.name}</div>
                        <div style={{fontSize:'10px', color: '#888'}}>{p.category}</div>
                      </td>
                      <td style={{textAlign: 'right'}}>
                        <div style={{fontSize: '12px'}}>RWF {p.price?.toLocaleString()}</div>
                        <button onClick={() => deleteProduct(p.id, p.name)} style={deleteBtn}><Trash2 size={12} /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- STYLES ---
const containerStyle = { padding: '15px', backgroundColor: '#f4f7f6', minHeight: '100vh', fontFamily: 'sans-serif' };
const headerStyle = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' };
const statsContainer = { display: 'flex', gap: '15px', marginBottom: '20px' };
const statCard = { flex: '1', backgroundColor: 'white', padding: '15px', borderRadius: '12px', display: 'flex', alignItems: 'center', gap: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.05)' };
const statIconBox = { padding: '10px', backgroundColor: '#e8f5e9', borderRadius: '8px' };
const statLabel = { margin: 0, fontSize: '10px', color: '#888', fontWeight: 'bold' };
const statValue = { margin: 0, fontSize: '16px', color: '#222' };
const tabBar = { display: 'flex', gap: '15px', marginBottom: '20px', borderBottom: '1px solid #ddd' };
const tabBtn = { background: 'none', border: 'none', padding: '10px 5px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '6px', color: '#666', borderBottom: '3px solid transparent' };
const activeTabBtn = { ...tabBtn, borderBottomColor: '#008ECC', color: '#008ECC', fontWeight: 'bold' };
const panelStyle = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)', marginBottom: '20px' };
const inventoryGridStyle = { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '20px' };
const inputStyle = { padding: '12px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px', width: '100%', boxSizing: 'border-box' };
const magicBtn = { backgroundColor: '#6200ea', color: 'white', border: 'none', borderRadius: '8px', padding: '0 15px', cursor: 'pointer', display: 'flex', alignItems: 'center', transition: '0.3s' };
const mainBtn = { padding: '12px', backgroundColor: '#008ECC', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', width: '100%' };
const logoutBtn = { display: 'flex', alignItems: 'center', gap: '6px', padding: '6px 12px', backgroundColor: '#212121', color: 'white', border: 'none', borderRadius: '6px', fontSize: '12px' };
const tableStyle = { width: '100%', borderCollapse: 'collapse' };
const thRow = { textAlign: 'left', borderBottom: '1px solid #eee', fontSize: '11px', color: '#888' };
const trStyle = { borderBottom: '1px solid #f9f9f9' };
const actionBtn = { border: 'none', backgroundColor: '#17a2b8', color: 'white', padding: '8px', borderRadius: '6px', cursor: 'pointer' };
const refreshBtn = { background: 'none', border: 'none', cursor: 'pointer', color: '#888' };
const searchContainer = { display: 'flex', alignItems: 'center', gap: '8px', backgroundColor: '#f0f2f5', padding: '6px 12px', borderRadius: '20px' };
const searchInput = { border: 'none', background: 'none', outline: 'none', fontSize: '12px', width: '120px' };
const deleteBtn = { backgroundColor: '#ff4d4d', color: 'white', border: 'none', padding: '5px', borderRadius: '4px', cursor: 'pointer' };

export default AdminDashboard;
