import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

// We accept addToCart as a prop from App.js
const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data, error } = await supabase.from('products').select('*');
      if (error) {
        console.error("Error fetching products:", error);
      } else if (data) {
        setProducts(data);
      }
      setLoading(false);
    };
    fetchProducts();
  }, []);

  if (loading) return (
    <div style={{ textAlign: 'center', padding: '100px', fontSize: '20px', color: '#008ECC' }}>
      Connecting to MegaMart Database...
    </div>
  );

  return (
    <div style={{ backgroundColor: '#F9F9F9', minHeight: '100vh', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Hero Banner */}
      <div style={{ 
        backgroundColor: '#212121', 
        color: 'white', 
        padding: '60px 50px', 
        marginBottom: '40px', 
        borderRadius: '0 0 50px 50px', 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center' 
      }}>
        <div>
          <h2 style={{ fontSize: '40px', marginBottom: '10px', lineHeight: '1.2' }}>
            Premium <span style={{ color: '#008ECC' }}>Electronics</span> <br/> Delivered in Kigali
          </h2>
          <p style={{ opacity: 0.8, fontSize: '18px' }}>Official MegaMart Store Rwanda</p>
        </div>
        <div style={{ fontSize: '100px', opacity: 0.1 }}>ðŸ“±</div>
      </div>

      {/* Main Content */}
      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 20px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '25px' }}>
          <h3 style={{ fontSize: '22px', fontWeight: 'bold', color: '#333' }}>
            Featured <span style={{ color: '#008ECC' }}>Smartphones</span>
          </h3>
          <span style={{ color: '#008ECC', fontWeight: 'bold', cursor: 'pointer' }}>View All Categories â†’</span>
        </div>

        {/* Product Grid */}
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fill, minmax(240px, 1fr))', 
          gap: '25px',
          paddingBottom: '50px'
        }}>
          {products.map((item) => (
            <div 
              key={item.id} 
              className="product-card"
              style={{ 
                backgroundColor: '#fff', 
                borderRadius: '20px', 
                padding: '20px', 
                position: 'relative', 
                border: '1px solid #eee',
                boxShadow: '0 4px 12px rgba(0,0,0,0.03)',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              {/* Blue Ribbon Discount */}
              <div style={{ 
                position: 'absolute', 
                top: 0, 
                right: 0, 
                backgroundColor: '#008ECC', 
                color: '#fff', 
                padding: '10px 15px', 
                borderBottomLeftRadius: '20px', 
                fontSize: '12px', 
                fontWeight: 'bold',
                zIndex: 1
              }}>
                10% OFF
              </div>

              {/* Image Container */}
              <div style={{ 
                height: '200px', 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center', 
                backgroundColor: '#fcfcfc', 
                borderRadius: '15px', 
                marginBottom: '15px' 
              }}>
                <img 
                  src={item.image_url || 'https://via.placeholder.com/150'} 
                  alt={item.name} 
                  style={{ maxHeight: '160px', maxWidth: '100%', objectFit: 'contain' }} 
                />
              </div>

              {/* Text Details */}
              <h4 style={{ fontSize: '15px', marginBottom: '10px', color: '#444', height: '40px', overflow: 'hidden', lineHeight: '1.4' }}>
                {item.name}
              </h4>
              
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: 'auto' }}>
                <span style={{ fontSize: '20px', fontWeight: 'bold', color: '#212121' }}>
                  RWF {item.price ? item.price.toLocaleString() : '0'}
                </span>
              </div>
              
              {/* Interaction Button */}
              <button 
                onClick={addToCart} // This triggers the counter in Navbar
                style={{ 
                  width: '100%', 
                  marginTop: '20px', 
                  backgroundColor: '#008ECC', 
                  color: 'white', 
                  border: 'none', 
                  padding: '14px', 
                  borderRadius: '12px', 
                  fontWeight: 'bold', 
                  cursor: 'pointer',
                  fontSize: '14px',
                  transition: 'background-color 0.2s'
                }}
                onMouseOver={(e) => e.target.style.backgroundColor = '#0072A3'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#008ECC'}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
