import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import { Mail, Lock, KeyRound, ArrowRight, ShieldCheck } from 'lucide-react';

const AuthPage = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [showOtpScreen, setShowOtpScreen] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otpToken, setOtpToken] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Step 1: Sign Up the user
  const handleSignUp = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.auth.signUp({ email, password });
    
    if (error) {
      alert(error.message);
    } else {
      alert('A 6-digit verification code has been sent to your email!');
      setShowOtpScreen(true); // Switch to the code entry screen
    }
    setLoading(false);
  };

  // Step 2: Verify the 6-digit code (Token)
  const handleVerifyCode = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    // verifyOtp uses the email and the 6-digit token from the email
    const { error } = await supabase.auth.verifyOtp({
      email,
      token: otpToken,
      type: 'signup',
    });

    if (error) {
      alert('Invalid code. Please check your email and try again.');
    } else {
      alert('Email verified successfully! Welcome to MegaMart.');
      navigate('/');
    }
    setLoading(false);
  };

  // --- Verification Screen UI ---
  if (showOtpScreen) {
    return (
      <div style={container}>
        <div style={authCard}>
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <ShieldCheck size={48} color="#008ECC" />
          </div>
          <h2 style={title}>Enter Verification Code</h2>
          <p style={subtitle}>Please enter the 6-digit code sent to <strong>{email}</strong></p>
          
          <form onSubmit={handleVerifyCode} style={form}>
            <div style={inputGroup}>
              <div style={inputWrapper}>
                <KeyRound size={18} color="#888" />
                <input 
                  type="text" 
                  placeholder="Enter 6-digit code" 
                  style={input} 
                  value={otpToken} 
                  onChange={(e) => setOtpToken(e.target.value)}
                  maxLength="6"
                  required 
                />
              </div>
            </div>
            <button type="submit" style={submitBtn} disabled={loading}>
              {loading ? 'Verifying...' : 'Verify & Create Account'}
            </button>
          </form>
          <button 
            onClick={() => setShowOtpScreen(false)} 
            style={{ background: 'none', border: 'none', color: '#666', marginTop: '15px', cursor: 'pointer', fontSize: '14px' }}
          >
            ‚Üê Back to Sign Up
          </button>
        </div>
      </div>
    );
  }

  // --- Standard Login/Sign-up UI ---
  return (
    <div style={container}>
      <div style={authCard}>
        <h2 style={title}>{isSignUp ? 'Join MegaMart' : 'Sign In'}</h2>
        <form onSubmit={isSignUp ? handleSignUp : (e) => {/* existing login logic */}} style={form}>
           {/* Email and Password inputs here */}
           <button type="submit" style={submitBtn}>
             {isSignUp ? 'Sign Up' : 'Sign In'} <ArrowRight size={18} />
           </button>
        </form>
        <p style={toggleText}>
          {isSignUp ? 'Have an account?' : "New to MegaMart?"}
          <span style={toggleLink} onClick={() => setIsSignUp(!isSignUp)}>
            {isSignUp ? ' Sign In' : ' Create Account'}
          </span>
        </p>
      </div>
    </div>
  );
};

// --- STYLES (Keep from previous versions) ---
const container = { display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh', backgroundColor: '#f9f9f9', padding: '20px' };
const authCard = { backgroundColor: '#fff', padding: '40px', borderRadius: '12px', boxShadow: '0 4px 20px rgba(0,0,0,0.1)', width: '100%', maxWidth: '400px', display: 'flex', flexDirection: 'column' };
const title = { fontSize: '22px', fontWeight: 'bold', marginBottom: '10px', textAlign: 'center' };
const subtitle = { fontSize: '14px', color: '#666', textAlign: 'center', marginBottom: '25px' };
const form = { display: 'flex', flexDirection: 'column', gap: '15px' };
const inputGroup = { display: 'flex', flexDirection: 'column', gap: '5px' };
const inputWrapper = { display: 'flex', alignItems: 'center', gap: '10px', padding: '12px', border: '1px solid #ddd', borderRadius: '8px' };
const input = { border: 'none', outline: 'none', width: '100%', fontSize: '15px' };
const submitBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '14px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px' };
const toggleText = { textAlign: 'center', marginTop: '20px', fontSize: '14px' };
const toggleLink = { color: '#008ECC', cursor: 'pointer', fontWeight: 'bold', marginLeft: '5px' };

export default AuthPage;
