import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { ShoppingCart, CheckCircle } from 'lucide-react';

const ProductDetailPage = ({ addToCart }) => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState('Black'); // Default style
  const [loading, setLoading] = useState(true);

  const colors = ['White', 'Pink', 'Blue', 'Black'];

  useEffect(() => {
    const fetchProduct = async () => {
      const { data } = await supabase.from('products').select('*').eq('id', productId).single();
      if (data) setProduct(data);
      setLoading(false);
    };
    fetchProduct();
  }, [productId]);

  const handleProceedToCheckout = () => {
    // Pass the selected style (color) to the cart/checkout
    addToCart({ ...product, selectedColor });
    navigate('/checkout'); 
  };

  if (loading) return <div style={{ textAlign: 'center', padding: '50px' }}>Loading...</div>;

  return (
    <div style={containerStyle}>
      <div style={detailGrid}>
        <img src={product.description} alt={product.name} style={imageStyle} />
        
        <div style={infoSection}>
          <h1>{product.name}</h1>
          <p style={priceText}>RWF {product.price?.toLocaleString()}</p>
          
          {/* STEP 1: CHOOSE STYLE */}
          <div style={{ margin: '20px 0' }}>
            <p style={{ fontWeight: 'bold' }}>Select Color:</p>
            <div style={{ display: 'flex', gap: '10px' }}>
              {colors.map(color => (
                <button 
                  key={color}
                  onClick={() => setSelectedColor(color)}
                  style={{
                    padding: '8px 16px',
                    borderRadius: '20px',
                    border: selectedColor === color ? '2px solid #008ECC' : '1px solid #ddd',
                    backgroundColor: selectedColor === color ? '#e6f7ff' : 'white',
                    cursor: 'pointer'
                  }}
                >
                  {color}
                </button>
              ))}
            </div>
          </div>

          <button onClick={handleProceedToCheckout} style={checkoutBtn}>
            <ShoppingCart size={20} /> Proceed to Shipping
          </button>
        </div>
      </div>
    </div>
  );
};

// Styles omitted for brevity but should match your theme
const containerStyle = { padding: '40px', maxWidth: '1000px', margin: 'auto' };
const detailGrid = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '40px' };
const imageStyle = { width: '100%', borderRadius: '15px' };
const priceText = { fontSize: '28px', color: '#ff4d4f', fontWeight: 'bold' };
const checkoutBtn = { width: '100%', padding: '15px', backgroundColor: '#008ECC', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', display: 'flex', justifyContent: 'center', gap: '10px' };

export default ProductDetailPage;
