import React from 'react';

const Navbar = ({ cartCount, openModal }) => { // 1. Added openModal prop
  return (
    <nav style={{ 
      backgroundColor: '#fff', 
      padding: '15px 50px', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'space-between', 
      boxShadow: '0 2px 5px rgba(0,0,0,0.05)', 
      position: 'sticky', 
      top: 0, 
      zIndex: 1000 
    }}>
      {/* Brand Name */}
      <h1 style={{ color: '#008ECC', fontSize: '24px', fontWeight: 'bold', margin: 0 }}>
        MegaMart
      </h1>

      {/* Search Bar */}
      <div style={{ flex: 1, margin: '0 40px' }}>
        <input 
          type="text" 
          placeholder="Search smartphones, laptops..." 
          style={{ 
            width: '100%', 
            padding: '10px 15px', 
            borderRadius: '8px', 
            border: '1px solid #ddd', 
            backgroundColor: '#f5f5f5',
            outline: 'none'
          }} 
        />
      </div>

      {/* Right Side Actions */}
      <div style={{ display: 'flex', gap: '20px', fontWeight: 'bold', alignItems: 'center' }}>
        <span style={{ cursor: 'pointer', fontSize: '14px', color: '#666' }}>Sign Up</span>
        
        {/* 2. The Clickable Cart Counter */}
        <div 
          onClick={openModal} // Triggers the Checkout Modal
          style={{ 
            backgroundColor: '#008ECC', 
            color: 'white', 
            padding: '8px 18px', 
            borderRadius: '25px', 
            fontSize: '14px', 
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            transition: 'transform 0.2s ease',
            boxShadow: '0 4px 10px rgba(0, 142, 204, 0.2)'
          }}
          onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
          onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
        >
          <span>ðŸ›’</span>
          Cart ({cartCount})
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
