import React from 'react';

const Navbar = ({ cartCount }) => { // Accept cartCount as a prop
  return (
    <nav style={{ backgroundColor: '#fff', padding: '15px 50px', display: 'flex', alignItems: 'center', justifyContent: 'space-between', boxShadow: '0 2px 5px rgba(0,0,0,0.05)', position: 'sticky', top: 0, zIndex: 1000 }}>
      <h1 style={{ color: '#008ECC', fontSize: '24px', fontWeight: 'bold', margin: 0 }}>MegaMart</h1>
      <div style={{ flex: 1, margin: '0 40px' }}>
        <input type="text" placeholder="Search..." style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1px solid #ddd', backgroundColor: '#f5f5f5' }} />
      </div>
      <div style={{ display: 'flex', gap: '20px', fontWeight: 'bold', alignItems: 'center' }}>
        <span>Sign Up</span>
        {/* The dynamic cart counter */}
        <div style={{ backgroundColor: '#008ECC', color: 'white', padding: '5px 12px', borderRadius: '20px', fontSize: '14px' }}>
          Cart ({cartCount})
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
