import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { ShoppingCart, CreditCard, ArrowLeft } from 'lucide-react';

const ProductDetailPage = ({ addToCart }) => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      const { data, error } = await supabase
        .from('products')
        .select('*')
        .eq('id', productId)
        .single();

      if (!error) setProduct(data);
      setLoading(false);
    };
    fetchProduct();
  }, [productId]);

  const handleBuyNow = (product) => {
    addToCart(product);
    navigate('/checkout'); // Direct path to payment
  };

  if (loading) return <div style={centerStyle}>Loading T500 Details...</div>;
  if (!product) return <div style={centerStyle}>Product not found.</div>;

  return (
    <div style={containerStyle}>
      <button onClick={() => navigate(-1)} style={backBtn}>
        <ArrowLeft size={18} /> Back to Shop
      </button>

      <div style={contentGrid}>
        {/* IMAGE SECTION */}
        <div style={imageWrapper}>
          {/* Note: Using product.description as the source because that's where your URL is stored */}
          <img 
            src={product.description || 'https://via.placeholder.com/400'} 
            alt={product.name} 
            style={imageStyle} 
          />
        </div>

        {/* INFO SECTION */}
        <div style={detailsStyle}>
          <span style={categoryLabel}>{product.category}</span>
          <h1 style={titleStyle}>{product.name}</h1>
          
          <div style={priceContainer}>
            <p style={priceStyle}>RWF {product.price?.toLocaleString()}</p>
            {product.old_price && (
              <p style={oldPriceStyle}>RWF {product.old_price.toLocaleString()}</p>
            )}
          </div>

          <div style={badgeContainer}>
            <div style={badge}>✓ In Stock</div>
            <div style={badge}>✓ Official Store</div>
          </div>

          <hr style={divider} />

          <p style={descText}>
            This {product.name} is available for immediate delivery in Kigali. 
            High-quality display and long battery life guaranteed.
          </p>

          <div style={buttonGroup}>
            <button 
              onClick={() => addToCart(product)} 
              style={cartBtn}
            >
              <ShoppingCart size={18} /> Add to Cart
            </button>

            <button 
              onClick={() => handleBuyNow(product)} 
              style={buyBtn}
            >
              <CreditCard size={18} /> Buy Now & Pay
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- Styles ---
const containerStyle = { padding: '40px 20px', maxWidth: '1100px', margin: 'auto', fontFamily: 'system-ui, sans-serif' };
const centerStyle = { padding: '100px', textAlign: 'center', fontSize: '18px', color: '#666' };
const contentGrid = { display: 'flex', flexWrap: 'wrap', gap: '50px', alignItems: 'start' };
const imageWrapper = { flex: '1', minWidth: '300px', backgroundColor: '#fff', padding: '10px', borderRadius: '15px', boxShadow: '0 10px 30px rgba(0,0,0,0.08)' };
const imageStyle = { width: '100%', borderRadius: '10px', display: 'block' };
const detailsStyle = { flex: '1.2', minWidth: '350px' };
const categoryLabel = { color: '#008ECC', fontWeight: 'bold', fontSize: '12px', textTransform: 'uppercase', letterSpacing: '1px' };
const titleStyle = { fontSize: '32px', margin: '10px 0 20px 0', color: '#222' };
const priceContainer = { display: 'flex', alignItems: 'baseline', gap: '15px', marginBottom: '20px' };
const priceStyle = { color: '#ff4d4f', fontSize: '32px', fontWeight: 'bold', margin: 0 };
const oldPriceStyle = { textDecoration: 'line-through', color: '#888', fontSize: '18px', margin: 0 };
const divider = { margin: '25px 0', border: '0', borderTop: '1px solid #eee' };
const descText = { lineHeight: '1.8', color: '#555', fontSize: '16px', marginBottom: '30px' };
const badgeContainer = { display: 'flex', gap: '10px', marginBottom: '20px' };
const badge = { backgroundColor: '#e6f7ff', color: '#1890ff', padding: '4px 10px', borderRadius: '4px', fontSize: '12px', fontWeight: '500' };
const backBtn = { border: 'none', background: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px', color: '#888', marginBottom: '20px', fontSize: '14px' };

const buttonGroup = { display: 'flex', flexDirection: 'column', gap: '12px' };
const cartBtn = { backgroundColor: '#f0f2f5', color: '#222', border: '1px solid #d9d9d9', padding: '16px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px', transition: '0.3s' };
const buyBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '16px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px', fontSize: '16px' };

export default ProductDetailPage;
