import React from 'react';
import { MessageCircle } from 'lucide-react';

const WhatsAppButton = () => {
  // Replace this with your actual phone number
  const phoneNumber = "250780000000"; 
  const message = "Hello MegaMart! I have a question about my order.";
  const whatsappLink = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;

  return (
    <a 
      href={whatsappLink} 
      target="_blank" 
      rel="noopener noreferrer" 
      style={buttonStyle}
      title="Chat with us on WhatsApp"
    >
      <MessageCircle size={30} />
      <span style={tooltip}>Chat with us</span>
    </a>
  );
};

// --- STYLES ---
const buttonStyle = {
  position: 'fixed',
  bottom: '30px',
  right: '30px',
  backgroundColor: '#25D366', // WhatsApp Green
  color: 'white',
  width: '60px',
  height: '60px',
  borderRadius: '50%',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
  cursor: 'pointer',
  zIndex: 1000,
  transition: 'transform 0.3s ease',
};

const tooltip = {
  position: 'absolute',
  right: '70px',
  backgroundColor: '#131921',
  color: 'white',
  padding: '5px 12px',
  borderRadius: '4px',
  fontSize: '12px',
  whiteSpace: 'nowrap',
  pointerEvents: 'none',
};

export default WhatsAppButton;
