import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { ChevronRight, ShieldCheck, Truck, RotateCcw } from 'lucide-react';

const BRAND_BLUE = '#008ECC';

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState('Black');
  const [selectedSize, setSelectedSize] = useState('Standard');

  useEffect(() => {
    const fetchProduct = async () => {
      const { data } = await supabase.from('products').select('*').eq('id', id).single();
      setProduct(data);
    };
    fetchProduct();
  }, [id]);

  if (!product) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading details...</div>;

  return (
    <div style={container}>
      <div style={breadcrumb}>
        Products <ChevronRight size={14} /> {product.category} <ChevronRight size={14} /> {product.name}
      </div>

      <div style={mainGrid}>
        {/* Left: Product Image */}
        <div style={imageSection}>
          <img src={product.image_url} alt={product.name} style={mainImg} />
        </div>

        {/* Right: Info & Options */}
        <div style={infoSection}>
          <h1 style={title}>{product.name}</h1>
          <p style={price}>RWF {product.price?.toLocaleString()}</p>
          <hr style={divider} />

          {/* Color Selection */}
          <div style={optionGroup}>
            <p style={optionLabel}>Color: <b>{selectedColor}</b></p>
            <div style={optionFlex}>
              {['Black', 'Silver', 'Space Gray'].map(color => (
                <div 
                  key={color} 
                  onClick={() => setSelectedColor(color)}
                  style={{...colorCircle, border: selectedColor === color ? `2px solid ${BRAND_BLUE}` : '1px solid #ddd'}}
                >
                  <div style={{...innerCircle, backgroundColor: color.toLowerCase().replace(' ', '')}} />
                </div>
              ))}
            </div>
          </div>

          {/* Size/Type Selection */}
          <div style={optionGroup}>
            <p style={optionLabel}>Type / Storage:</p>
            <div style={optionFlex}>
              {['128GB', '256GB', '512GB'].map(size => (
                <button 
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  style={{...sizeBtn, borderColor: selectedSize === size ? BRAND_BLUE : '#ddd', color: selectedSize === size ? BRAND_BLUE : '#333'}}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          <div style={actionRow}>
            <button style={buyNowBtn}>Buy Now</button>
            <button style={addToCartBtn}>Add to Cart</button>
          </div>

          <div style={trustBadges}>
            <div style={badge}><ShieldCheck size={18} /> 1 Year Warranty</div>
            <div style={badge}><Truck size={18} /> Fast Delivery</div>
            <div style={badge}><RotateCcw size={18} /> 7 Day Return</div>
          </div>
        </div>
      </div>

      {/* Specifications Table */}
      <div style={specsSection}>
        <h3 style={specsTitle}>Technical Specifications</h3>
        <table style={table}>
          <tbody>
            <tr><td style={tdLabel}>Brand</td><td style={tdVal}>MegaMart Official</td></tr>
            <tr><td style={tdLabel}>Model</td><td style={tdVal}>{product.name}</td></tr>
            <tr><td style={tdLabel}>Category</td><td style={tdVal}>{product.category}</td></tr>
            <tr><td style={tdLabel}>Condition</td><td style={tdVal}>New (Verified)</td></tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

// --- Styles ---
const container = { padding: '20px 8%', backgroundColor: '#fff', minHeight: '100vh' };
const breadcrumb = { display: 'flex', alignItems: 'center', gap: '5px', fontSize: '13px', color: '#888', marginBottom: '20px' };
const mainGrid = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '50px' };
const imageSection = { border: '1px solid #eee', borderRadius: '8px', padding: '20px', textAlign: 'center' };
const mainImg = { maxWidth: '100%', maxHeight: '450px', objectFit: 'contain' };
const infoSection = { display: 'flex', flexDirection: 'column' };
const title = { fontSize: '28px', margin: '0 0 10px 0', fontWeight: '700' };
const price = { fontSize: '24px', color: BRAND_BLUE, fontWeight: '800', margin: '0 0 20px 0' };
const divider = { border: 'none', borderTop: '1px solid #eee', margin: '20px 0' };
const optionGroup = { marginBottom: '25px' };
const optionLabel = { fontSize: '14px', marginBottom: '10px' };
const optionFlex = { display: 'flex', gap: '10px' };
const colorCircle = { padding: '3px', borderRadius: '50%', cursor: 'pointer' };
const innerCircle = { width: '25px', height: '25px', borderRadius: '50%' };
const sizeBtn = { padding: '8px 20px', border: '1px solid', backgroundColor: '#fff', borderRadius: '4px', cursor: 'pointer', fontSize: '14px' };
const actionRow = { display: 'flex', gap: '15px', marginTop: '20px' };
const buyNowBtn = { flex: 1, padding: '15px', backgroundColor: BRAND_BLUE, color: '#fff', border: 'none', borderRadius: '5px', fontWeight: '700', cursor: 'pointer' };
const addToCartBtn = { flex: 1, padding: '15px', backgroundColor: '#FFF0E6', color: BRAND_BLUE, border: `1px solid ${BRAND_BLUE}`, borderRadius: '5px', fontWeight: '700', cursor: 'pointer' };
const trustBadges = { display: 'flex', justifyContent: 'space-between', marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '20px' };
const badge = { display: 'flex', alignItems: 'center', gap: '5px', fontSize: '12px', color: '#666' };
const specsSection = { marginTop: '50px', borderTop: '2px solid #eee', paddingTop: '30px' };
const specsTitle = { fontSize: '18px', fontWeight: '700', marginBottom: '20px' };
const table = { width: '100%', borderCollapse: 'collapse' };
const tdLabel = { padding: '12px', backgroundColor: '#f9f9f9', width: '200px', fontWeight: '600', border: '1px solid #eee' };
const tdVal = { padding: '12px', border: '1px solid #eee' };

export default ProductDetails;
