import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShieldCheck, ChevronDown, CheckCircle, Star, Globe, Smartphone } from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [rating, setRating] = useState(0);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  
  const countries = [
    'Rwanda', 'United States', 'Kenya', 'Uganda', 'Tanzania', 
    'United Kingdom', 'Canada', 'France', 'Germany', 'China', 'India'
  ];

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000; 
  const total = subtotal + delivery;

  const handleFinishPayment = () => {
    // Logic for payment processing would go here
    setCart([]); // Clear cart after success
    setStep(3); // Move to success screen
  };

  // --- STEP 3: SUCCESS & RATING ---
  if (step === 3) {
    return (
      <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Your Global Order is Placed!</h1>
        <p>Shipping to {selectedCountry} is being processed.</p>
        
        <div style={ratingBox}>
          <h3>Rate your experience</h3>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
            {[1, 2, 3, 4, 5].map(s => (
              <Star 
                key={s} 
                fill={rating >= s ? "#ffc107" : "none"} 
                color="#ffc107" 
                onClick={() => setRating(s)} 
                style={{cursor:'pointer'}}
              />
            ))}
          </div>
          {rating > 0 && <p style={{color: '#28a745', marginTop: '10px'}}>Thank you for your feedback!</p>}
          <button onClick={() => navigate('/')} style={continueBtn}>Return to Shop</button>
        </div>
      </div>
    );
  }

  // --- STEP 1 & 2: SHIPPING & SUMMARY ---
  return (
    <div style={pageWrapper}>
      <div style={mainContent}>
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={24} style={{marginRight: '10px'}}/> Shipping Information</h2>
          
          <div style={formRow}>
            <div style={inputGroupFull}>
              <label style={labelStyle}>Country or region</label>
              <select 
                style={selectInput} 
                value={selectedCountry}
                onChange={(e) => setSelectedCountry(e.target.value)}
              >
                {countries.map(country => (
                  <option key={country} value={country}>{country}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div style={formRow}>
            <input style={inputHalf} placeholder="First name" />
            <input style={inputHalf} placeholder="Last name" />
          </div>

          <div style={formRow}>
            <input style={inputFull} placeholder="Street address" />
          </div>

          <div style={formRow}>
            <input style={inputThird} placeholder="City" />
            <input style={inputThird} placeholder="State / Province" />
            <input style={inputThird} placeholder="ZIP / Postal code" />
          </div>

          <div style={formRow}>
            <div style={phoneWrapper}>
              <input style={phoneInput} placeholder="International Phone number (e.g. +250...)" />
            </div>
          </div>
          <p style={helperText}>Required for international shipping updates.</p>
        </div>

        <div style={sectionCard}>
          <h2 style={sectionHeader}><Smartphone size={24} style={{marginRight: '10px'}}/> Payment Method</h2>
          <div style={activeMethod}>
            <div style={{fontWeight: 'bold'}}>Mobile Money / International Card</div>
            <div style={{fontSize: '12px', color: '#666'}}>Secure global payment gateway</div>
          </div>
        </div>
      </div>

      {/* SIDEBAR: Order Summary */}
      <div style={sidebar}>
        <div style={summaryCard}>
          <h2 style={summaryTitle}>Order Summary</h2>
          <div style={summaryRow}>
            <span>Items ({cart.length})</span>
            <span>RWF {subtotal.toLocaleString()}</span>
          </div>
          <div style={summaryRow}>
            <span>Shipping to {selectedCountry}</span>
            <span>RWF {delivery.toLocaleString()}</span>
          </div>
          <hr style={divider} />
          <div style={totalRow}>
            <span>Order total</span>
            <span>RWF {total.toLocaleString()}</span>
          </div>
          
          <button style={confirmBtn} onClick={handleFinishPayment}>
            Place Global Order
          </button>
          
          <div style={protectionBox}>
            <ShieldCheck size={20} color="#0053a0" />
            <span>Purchase protected by <b>MegaMart Global Guarantee</b></span>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- STYLES (Fixed All Missing Definitions) ---
const pageWrapper = { display: 'flex', gap: '40px', padding: '40px 10%', backgroundColor: '#f8f9fa', minHeight: '100vh', fontFamily: 'Arial, sans-serif' };
const mainContent = { flex: 2 };
const sidebar = { flex: 1 };
const sectionCard = { backgroundColor: 'white', padding: '25px', borderRadius: '12px', border: '1px solid #eee', marginBottom: '20px' };
const sectionHeader = { fontSize: '22px', fontWeight: '600', marginBottom: '20px', display: 'flex', alignItems: 'center', color: '#333' };
const formRow = { display: 'flex', gap: '12px', marginBottom: '15px' };
const inputGroupFull = { display: 'flex', flexDirection: 'column', width: '100%' };
const labelStyle = { fontSize: '13px', color: '#707070', marginBottom: '5px', fontWeight: 'bold' };
const selectInput = { padding: '15px', border: '1px solid #cbcbcb', borderRadius: '8px', width: '100%', backgroundColor: '#fff' };
